css:
/* i really want this to be global */
@charset "UTF-8";
/*:/// v1.0 | 2014 \\\:*/

* {
outline: none!important;
background: rgba(0, 0, 0, 0);
background-color: rgba(0, 0, 0, 0);
border-color: rgba(128, 128, 128, 0.5)!important;
-webkit-font-smoothing: antialiased;
}
body,span,applet,h1,h2,h3,h4,h5,h6,p,blockquote,pre,abbr,acronym,address,big,cite,code,del,dfn,em,font,ins,kbd,q,s,samp,small,strike,strong,sub,sup,tt,var,b,u,i,center,dl,dt,dd,ol,ul,li,fieldset,form,label,legend,table,caption,tbody,tfoot,thead,tr,th,td,button{background-color:transparent;box-sizing: border-box;}
body{line-height:1;}
ol,ul{list-style:none;}
blockquote,q{quotes:none;}
blockquote:before,blockquote:after,q:before,q:after{content:'';content:none;}
:focus{outline:0;}
ins{text-decoration:none;}
del{text-decoration:line-through;}
table{border-collapse:collapse;border-spacing:0;}
/*set*/
table,caption,tbody,tfoot,thead,tr {
border-collapse: collapse;
background-color: rgba(255, 255, 255, 0.25);
background-image: -webkit-linear-gradient(left,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.35) 50%,rgba(0, 0, 0, 0.5) 100%);
vertical-align: top;
}
th,td {
background-color: rgba(0, 0, 0, 0.5);
background-image: -webkit-linear-gradient(top,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.35) 50%,rgba(0, 0, 0, 0.5) 100%);
}
html {
background:-webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(28, 28, 28, 1)), color-stop(50%,rgba(163, 163, 163, 1)), color-stop(100%,rgba(28, 28, 28,1)));
background-attachment: fixed;
}
body {
background:rgba(0, 0, 0, 0) no-repeat fixed 0 0;
color:#D3D3D3;
}
h1,h2,h3,h4,h5,h6 {
background-color: rgba(0, 0, 0, 0.5)!important;
background: -webkit-gradient(linear, left top, right top, color-stop(0%,rgba(0, 0, 0, 0.5)), color-stop(50%,rgba(251, 251, 251, 0.5)), color-stop(100%,rgb(0, 0, 0, 0.5)));
background-image: -webkit-linear-gradient(left,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
color:#cfdefc!important;
text-shadow: 0 1px 0 rgba(0, 0, 0, 0.5), 0 2px 1px rgba(0, 0, 0, 0.4), 0 3px 2px rgba(0, 0, 0, 0.3), 0 4px 3px rgba(0, 0, 0, 0.2);
}
button, a.button {
background-color: rgba(0, 0, 0, 0)!important;
background-image:-webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0, 0, 0, 0.5)), color-stop(50%,rgba(250, 250, 250, 0.5)), color-stop(100%,rgba(0, 0, 0, 0.5)));
color:#cfdfff!important;
text-shadow: 0 1px 0 rgba(0, 0, 0, 0.5), 0 2px 1px rgba(0, 0, 0, 0.4), 0 3px 2px rgba(0, 0, 0, 0.3), 0 4px 3px rgba(0, 0, 0, 0.2);
}
strong, b {
background-color: rgba(0, 0, 0, 0)!important;
background-image:-webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0, 0, 0, 0.5)), color-stop(50%,rgba(250, 250, 250, 0.5)), color-stop(100%,rgba(0, 0, 0, 0.5)));
font-weight: bold;
color: rgb(97, 167, 221)!important;
}
textarea, input {
background-color: rgba(0, 0, 0, 0.5)!important;
background-image: -webkit-linear-gradient(left,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.25) 50%,rgba(0, 0, 0, 0.5) 100%);
background-clip: padding-box;
background-origin: content-box;
color: rgba(255, 255, 255, 0.85)!important;;
resize: none; 
white-space: pre-wrap;
word-wrap: break-word;
}
div,object,iframe,video {
outline: none!important;
box-sizing: content-box;
-webkit-box-sizing: content-box;
}
article, aside, footer, header, hgroup, main, nav, section {
background-color: rgba(0, 0, 0, 0)!important;
background: -webkit-linear-gradient(left,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%)!important;
background-image: -webkit-linear-gradient(left,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%)!important;
background-clip: padding-box;
background-origin: border-box;
}
div:not([onclick]) {
background-color: rgba(0, 0, 0, 0.5)!important;
background: -webkit-linear-gradient(left top,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
background-image: -webkit-linear-gradient(left top,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
background-size:auto;
background-repeat: repeat;
background-clip: content-box;
background-origin: padding-box;
color: rgba(255, 255, 255, 0.8);
text-shadow:-1px 1px rgba(55,55,55,0.5);
outline-offset: inherit;
outline-color: dimgray;
outline-style: inset;
outline-width: initial;
}
div:not([class]) {
background-color: rgba(0, 0, 0, 0.5)!important;
background: -webkit-linear-gradient(top ,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
background-image: -webkit-linear-gradient(top,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.35) 50%,rgba(0, 0, 0, 0.5) 100%);
background-size: auto;
background-repeat: repeat;
background-clip: padding-box;
background-origin: content-box;
color: rgba(255, 255, 255, 0.8);
text-shadow:0 -1px 1px rgba(55,55,55,0.5);
outline-offset: inherit;
outline-color: dimgray;
outline-style: outset;
outline-width: initial;
}
div:not([emty]) {
background-color: rgba(0, 0, 0, 0.5)!important;
background: -webkit-linear-gradient(top left,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
background-image: -webkit-linear-gradient(top left,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.35) 50%,rgba(0, 0, 0, 0.5) 100%);
background-size: auto;
background-repeat: repeat;
background-clip: padding-box;
background-origin: padding-box;
color: rgba(255, 255, 255, 0.8);
outline-offset: inherit;
outline-color: dimgray;
outline-style: inset;
outline-width: initial;
}
div:not([id]) {
background-color: rgba(0, 0, 0, 0.5)!important;
background: -webkit-linear-gradient(top right,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
background-image: -webkit-linear-gradient(top right,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.35) 50%,rgba(0, 0, 0, 0.5) 100%);
background-size: auto;
background-repeat: repeat;
background-clip: padding-box;
background-origin: content-box;
color: rgba(255, 255, 255, 0.8);
text-shadow:1px -1px rgba(55,55,55,0.5);
outline-offset: inherit;
outline-color: dimgray;
outline-style: outset;
outline-width: initial;
}
span:not(emty) {
background-color: rgba(0, 0, 0, 0.5)!important;
background-image: -webkit-linear-gradient(left,rgba(0, 0, 0, 0.5) 0%,rgba(235, 245, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
background-clip: padding-box;
background-origin: content-box;
color: rgba(215, 235, 255, 0.85)!important;
outline-offset: inherit;
outline-color: dimgray;
outline-style: ridge;
outline-width: medium;
}
span:not([class]) {
background-color: rgba(0, 0, 0, 0.5)!important;
background-image: -webkit-linear-gradient(left top,rgba(0, 0, 0, 0.25) 0%,rgba(230, 240, 250, 0.5) 50%, rgba(0, 0, 0, 0.5) 100%);
background-clip: content-box;
background-origin: padding-box;
color: rgba(225, 225, 255, 0.85)!important;
outline-offset: inherit;
outline-color: dimgray;
outline-style: initial;
outline-width: initial;
}
ol:not([onclick]) {
background-color: rgba(0, 0, 0, 0.5)!important;
background: -webkit-linear-gradient(left top,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
background-image: -webkit-linear-gradient(left top,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%)!important;
background-clip: padding-box;
background-origin:border-box;
color: rgba(255, 255, 255, 0.8);
}
li:not([class]) {
background-color: rgba(0, 0, 0, 0.5)!important;
background-image: -webkit-linear-gradient(left,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%)!important;
background-clip: padding-box;
background-origin: content-box;
color: rgba(255, 255, 255, 0.8);
}
li:not([emty]) {
background-color: rgba(0, 0, 0, 0.5)!important;
background-image: -webkit-linear-gradient(right,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%)!important;
background-clip: padding-box;
background-origin: content-box;
color: rgba(255, 255, 255, 0.8);
}
ol:not([id]) {
background-color: rgba(0, 0, 0, 0.5)!important;
background: -webkit-linear-gradient(left top,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
background-image: -webkit-linear-gradient(left top,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%)!important;
background-clip: padding-box;
background-origin: content-box;
color: rgba(255, 255, 255, 0.8);
}
li > a {
background-color: rgba(0, 0, 0, 0.65)!important;
background: -webkit-linear-gradient(top,rgba(255, 255, 255, 0.5) 0%,rgba(0, 0, 0, 0.5) 50%,rgba(0, 0, 0, 0.85) 100%);
background-image: -webkit-linear-gradient(top,rgba(255, 255, 255, 0.5) 0%,rgba(0, 0, 0, 0.5) 50%,rgba(0, 0, 0, 0.85) 100%)!important;
background-clip: content-box;
background-origin: border-box;
color: rgba(255, 255, 255, 0.8)!important;
}
li > a > span {
background-color: rgba(0, 0, 0, 0.5)!important;
background: -webkit-linear-gradient(left,rgba(0, 0, 0, 0.5) 0%,rgba(254, 255, 255, 0.5) 50%,rgba(0, 0,0, 0.5) 100%);
background-image: -webkit-linear-gradient(left,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%)!important;
background-clip: content-box;
background-origin: content-box;
color: rgba(255, 255, 255, 1.0)!important;
}
ul:not([emty]):not([onclick]) {
background-color: rgba(0, 0, 0, 0.5)!important;
background-image: -webkit-linear-gradient(top,rgba(0, 0, 0, 0.5) 0%,rgba(252, 252, 252, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
background-clip: padding-box;
background-origin:content-box;
color: rgba(255, 255, 255, 0.8);
}
ul:not([id]):not([class]) {
background-color: rgba(0, 0, 0, 0.5)!important;
background-image: -webkit-linear-gradient(top,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
}
p > a {
color: rgb(219, 219, 219)!important;
}
p {
color: rgb(185, 185, 185)!important;
background-color: rgba(31, 31, 31, 0.5)!important;
background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(23, 30, 30, 0.5)), color-stop(100%,rgba(8, 11, 11, 0.5)));
font: normal 12px/20px helvetica,arial,sans-serif;
}
a {
background-color: rgba(0, 0, 0, 0)!important;
color: rgb(51, 51, 51);
color: rgb(185, 203, 234)!important;
cursor: pointer;
text-shadow: 0 1px 0 rgba(0, 0, 0, 0.5), 0 2px 1px rgba(0, 0, 0, 0.4), 0 3px 2px rgba(0, 0, 0, 0.3), 0 4px 3px rgba(0, 0, 0, 0.2);
text-decoration: none;
}
a:hover{
outline: none!important;
color:rgba(255,255,255,1)!important;
text-shadow:0 1px 1px rgba(55,55,55,0.5);
-webkit-transition:all 250ms;
}
img {
border-radius: 3px;
box-shadow: inset 0 0 1px rgba(225, 225, 255, 0.6), 0px 10px 15px rgba(2, 2, 2, 0.5);
padding: 2px 2px 1px;
background-color: rgba(0, 0, 0, 0);
background-image: -webkit-gradient(linear, left top, right bottom, color-stop(0%,rgba(82, 82, 82, 1)), color-stop(50%,rgba(182, 182, 182, 1)), color-stop(100%,rgba(25, 25, 25, 1)));
}
a:hover img {
  -webkit-transform:scale(1.1);
  -webkit-transition: -webkit-transform 240ms;
}
div#contentCol.homeFixedLayout {
  background-color: rgba(0, 0, 0, 0.0)!important;
}
.fbPhotoSnowlift .stage .fbPhotosPhotoTagboxes {
border: rgba(255, 255, 255, 0.1) 1px solid;
-webkit-box-sizing: border-box;
font-size: 11px;
display: none;
}
#navSearch .uiSearchInput input {
padding-top: 0px;
color: rgb(0, 0, 0);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(182, 182, 182, 0.5)), color-stop(50%,rgba(1, 1, 1, 0.5)), color-stop(100%,rgba(255, 255, 255, 0.5)));
}
.uiTypeahead .uiSearchInput {
background-color: rgba(255, 255, 255, 0);
}
.uiMentionsInput .mentionsTextarea, .uiMentionsInput .mentionsTypeahead {
color:rgba(255, 255, 255, 0.8);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(214, 210, 212, 0.5)), color-stop(100%,rgba(38, 32, 33, 0.5)));
zoom: 1;
}
