css:
@-moz-document url-prefix("http://funkysouls.com/2/") {
iframe,h3, td.td0bottom,
td.td0left,td.td0right,
div.bline>font,div.bline,
div.txt>br:nth-of-type(1),
div.txt>br:nth-of-type(2),
div.txt>br:nth-of-type(3),
div.txt>br:nth-of-type(4),
img[src*="arrow.gif"],
a[href*="journal.php"],
a[href*="funkyimg.com"],
a[href*="gallery.funkysouls"]
{
	display:none
}

form
{
	padding-top:5px;
}

input[type=text]
{
	height:24px;
	padding-left:5px;
    background:#f5f5f5;
	text-decoration: none;
    border-radius: 5px;
	border:1px solid #aaa;
	outline:none;
}

input[type=text]:focus
{
    background:#fefefe;
}

input[type=submit]
{
	margin-right:-20px;
	outline:none;
	box-shadow:inset 0px 1px 0px 0px #97c4fe;
	background:linear-gradient(to bottom, #3d94f6 5%, #1e62d0 100%);
	background-color:#3d94f6;
	border-radius:6px;
	border:1px solid #337fed;
	cursor:pointer;
	color:#fff;
	padding:4px 18px;
	font-weight:bold;
	text-decoration:none;
	text-shadow:0px 1px 0px #1570cd;
}

input[type=submit]:hover
{
	background:linear-gradient(to bottom, #1e62d0 5%, #3d94f6 100%);
	background-color:#1e62d0;
}

input[type=submit]:active
{
	border:1px solid #B90000;
	background:linear-gradient(to bottom, #B90000 5%, #e00 100%);
	background-color:#B90000;
}

a[href="http://funkytorrents.com"],
a[href="http://forum.funkysouls.com"],
a[href="http://releases.funkysouls.com"]
{
	padding-left:4px;
	border-left:1px solid #ddd
}


a[href="http://forum.funkysouls.com"]
{
	border:none
}

.txt img
{
	float:left;
	margin:0 15px 0 -315px;
	width:300px;
	height:auto
}

div.txt>b:nth-of-type(1)
{
	color:#B90000;
	font-size:18px;
	font-family:Arial
}

body
{
	margin:0 auto;
	width:850px
}

div.txt
{
	overflow:hidden;
	margin:20px 0;
	padding:15px 0 15px 330px;
	border:#ddd 1px solid;
	border-radius:5px;
	background:#f7f7f7;
	text-align:left;
}

div.txt:hover
{
	border:1px solid #fff;
	background:#fff;
    box-shadow:
    0 0 0 5px #fff,
    0 0 0 6px #B90000,
    0 0 15px 10px rgba(0,0,0,0.3);
}

div.txt>a
{
	padding:2px 5px;
	border-radius:10px;
	background:#aaa;
	color:#fff;
	text-shadow:0 0 2px #000;
	line-height:25px;
}

div.txt>a:hover
{
	background:#ccc;
	text-decoration:none
}

div.txt>a[href*="view=pid"]
{
	background:#B90000;
}

div.txt>a[href*="view=pid"]:hover
{
	background:#e00;
}
}
