css:
@-moz-document domain("primeradivision.pl"), domain("anglia.goal.pl"), domain("seriea.pl"), domain("bundesliga.goal.pl"), domain("goal.pl") {
body {
 font: normal 120%/140% Cambria,georgia,serif; 
 background: #fff !important;
}
a:hover {
  text-decoration:underline;
}
div.newsy {
 width:640px; 
}
div.news, div.rest {
 width: 100%;
 box-sizing:border-box;
}
div.tresc {
 width: 470px;
 padding-left: 140px;
 font-size:100%;
 position:relative;
 min-height: 140px;
}
div.foto {
 position:absolute;
 top: 0;
 left: 0;
}
p.tytul {
 font: bold 120%/150% Cambria,georgia,serif;
 margin-left: 5px;
}
p.tytul a:hover {
 text-decoration:underline;
}
div.nagl, div.nagl2 {
 font: bold 100%/130% Cambria,georgia,serif;
 color: #a00;
 background: #fff;
 height: 30px;
 margin: 20px 0 10px;
}
div.nagl2 {
 width: 100%;
 padding: 3px 0; 
}
div.dot_news {
 background: #fff;
 margin: 0 0 10px;
}
div.league {
 width:320px; 
}
div.tlo {
 width: 100%;
}
div.tlo table {
 width: 100%; 
}
div.othernews, div.banner {
 display:none;
}
div.content div:not(.logowanie) a {
 background: transparent;
}
div.top {
  background: transparent;
}
div.top > a:after {
 content: attr(href);
 color: #a00;
 font-weight: bold;
 font-size: 150%;
}
div.content {
  position: relative;
}
div.clubs, div.menu {
 background: #fff;
 height: auto;
 min-height: 25px;
 font: normal 0px/0px Cambria,georgia,serif;
 color: #fff;
 text-align:left;
 margin-left: 5px;
 position: absolute;
 font-size: 0;
 z-index:99999;
 padding: 0 10px;
 width: 150px;
}
div.clubs:before, div.menu:before {
 content: 'Kluby: ';
 color: #000;
 font-size: 18px;
 line-height: 30px;
}
div.clubs a, div.menu li {
  display: none;
  margin: 0 -10px;
  padding: 0 10px !important;
  font-size: 16px !important;
  border-right: 1px solid #aaa;
  border-left: 1px solid #aaa;
}
div.clubs a:first-child, div.menu li:first-child {
  border-top: 1px solid #aaa;
}
div.clubs a:last-of-type, div.menu li:last-of-type {
  border-bottom: 1px solid #aaa;
}
div.menu ul li {
  color: #fff;
  font-size:0;
}
div.clubs a:before, div.menu ul li a:before {
  content: 'Â» ';
}
div.menu ul li a {
  margin-left:-10px;
  line-height: 150%
}
div.clubs:hover a, div.menu:hover li {
  display: block;
}
div.clubsu {
 display: none;
}
div.clubs a, div.clubs a:hover, div.menu ul {
 font: normal 90%/150% Cambria,georgia,serif;
 padding: 0;
 color: #000;
}
div.clubs a:hover {
  text-decoration: underline;
  background: #fff !important;
}
div.menu ul a, div.menu ul a:hover {
  font: normal 90%/150% Cambria,georgia,serif;
}
div.menu ul li {
  margin: 0 !important;
  padding: 0 10px !important;
  float: none !important;
}
div.menu {
  left: 180px; 
}
div.menu:before {
  content: 'Menu:';
}
div.menu + div.menu {
  left: 370px; 
}
div.menu + div.menu:before {
  content: 'Inne ligi:';
}
div.newsy div[style]:not([class]) {
  width: 557px !important;
  padding: 0 !important;
  background: transparent !important;
  font-size: 95%;
  line-height: 1.3;
}
div.newsy br + div[style]:not([class]),
div.newsy div[style*=clear]:empty + div[style]:not([class]){
  width: 60px !important; 
}
div.newsy table {
  border-spacing: 0;
}
div.newsy div[style*=clear]:empty {
  height: 30px !important;
}
ul[style] li a[style*=font] {
  font-size: 100% !important 
}
div.news2 {
  width: 90%;
  background: transparent;
  padding-bottom: 20px;
}
p.tytul2, p.tresc2 {
 font: normal 90%/140% Cambria,georgia,serif;
}
p.tytul2 {
  font-weight:bold;
  font-size: 100%;
}
p.wiecej {
  width:95%;
  color:#aaa;
}
.menu span.bold {
  font: bold 90%/140% Cambria,georgia,serif !important;
  color:#000;
}
.goal_wrap #goal_header {
  background: #000;
  font-family: Cambria, Georgia, serif;
  font-size: 80%;
}
#goal_header .goal_first {
  display:none;
}
  #goal_header #goal_navTop ul {
    padding: 0 10px !important;
  }
#goal_header #goal_navTop ul li a {
  color: #fff;
}
#goal_header #goal_navTop ul li {
    background: transparent;
  }
#goal_header #goal_navTop {
    height: 23px;
    z-index:999999999
  }
#goal_header #goal_navTop ul li ul {
    left: auto;
    background: #000;
    z-index:9999999;
  }
  #goal_header #goal_navTop ul li ul li {
    position:relative;
    z-index:999999999999;
  }
  
  div.newsy .tresc div[style*='text-align'] {
      text-align: left !important;
      font-size: 130% !important;
    }
input.text, input.button {
  font: normal 14px/22px Cambria, Georgia, serif;
  background: #fff;
  border: 1px solid #aaa;
  width: auto;
  height: auto;
}
input.button {
  background: #000;
  border: 1px solid #fff;
  box-shadow: 0 0 3px 0 #333;
  cursor:pointer;
  padding: 4px 20px;
}
div.log {
  width: 60px;
}
.logowanie input.text {
  width: 150px;
}
#reklama_arch, a[href*=fortunaaffiliates], .stopka, .linki, #reklama_top, #infoCookies {
  display:none;
}
div.league {
  padding-bottom: 40px;
  font-size: 80%;
  width: 280px;
  margin-left: 30px;
  overflow:hidden;
}
div.news2 + div[style] {
  font-size: 100% !important;
  margin-bottom: 40px !important;
}
div.tlo a, div.rest a, a, div.clubs a, div.clubs a:hover, div.logowanie a {
  color: #9c0001;
}
.red {
    color: #aaa;
  }
div.logowanie {
  color: #222;
}
tr[bgcolor], tr:nth-child(2n), div.tlo {
  background: #fff !important;
}
tr:nth-child(2n+1)  {
  background: #fafafa !important;
}
.wiersz1 td, .wiersz2 td {
    border: none !important;
  }
  div.league .nagl2 {
    color:#000;
    font-size: 120%;
    margin: 30px 0 -5px;
    text-indent: -13px;
  }
}
