css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("launchpad.net") {

* {
background-color: #111 !important;
color: #646666 !important;
border-color: #202222 !important;
opacity: 1 !important;
}

[class = "code insert"], .diff-added {
background-color: #449944 !important;
color: #000 !important;
}

[class = "code delete"], .diff-removed {
background-color: #994444 !important;
color: #000 !important;
}

[class = "infoContainer"] {
background-color: #111 !important;
background: none !important;
border: none !important;
}

[id = "list-files"] {
background-color: #202222 !important;
border: none !important;
}

[class = "files"] {
background: none !important;
}
  
  [class="branch-deco"] {
    background-color:  greenyellow !important;
    color: black !important;
  }

img {
opacity: .1 !important;
transition: opacity .2s;
background-color: #111 !important;
}

img:hover, a:hover img {
opacity: .5 !important;
}

input, select, div, span {
background-color: #111 !important;
background: none !important;
border: none !important;
opacity: 1 !important;
}

.yui3-pretty-overlay
{
opacity: 1 !important;
background: none !important;
background-color: #111 !important;
border: none !important;
}

.yui3-editable_text-text.yui3-editable_text-text-hover
{
background: none !important;
background-color: #111 !important;
border: none !important;
}

div#cgit table.list td a.ls-dir,
div#cgit table.diff td div.head {
	color: slategray!important;
  font-weight: normal !important;
}

[class="mode"] {
	background-color: none !important;
  color: blue !important;
	text-decoration: none;
}
  
  td.upd a {
    color: #5555ff !important;
  }
  
  [class="head"] {
    font-weight: bold !important;
  }
  
  [class="hunk"] {
    color: #5555ff !important;
  }
  
  [class="del"] {
    color:  #B20400 !important;
  }
  
  [class="add"] {
    color: #3AAD2A!important;
  }
  
  div#cgit table.diffstat td.graph td.add {
	background-color: #3AAD2A !important;
}
  
div#cgit table.diffstat td.graph td.rem {
	background-color: darkred !important;
}
 
  div#cgit div.diffstat-header a {
	font-weight: bold;
	padding-top: 1.5em;
   color: blue !important;
  }
}
