css:
@-moz-document domain("37.251.31.61") {
body {
background-image: url(http://aijt.nl/uploads/emplix/stylish/bg_page.jpg);
background-repeat: repeat-x;
background-color: #9f2200;
color: #242424;
}
input, textarea, select {
border: 1px solid #989898;
background-color: #F0F0F0;
font: normal 10px Verdana, Arial, Helvetica, sans-serif;
color: #666666;
}
#page {
width: 1016px;
  background: url(http://aijt.nl/uploads/emplix/stylish/bg_content.png);
}
#stats {
display: none;
}
#header {
background: url('http://aijt.nl/uploads/emplix/stylish/header stylish.png')!important;
width: 1016px;
height: 178px;
padding: 0;
position: relative;
}
#contentpuffer {
background: transparent;
width: 1016px;
}
#left {
width: 230px; color: #FFF;
}
#right {
width: 786px;
float: left;
}
#right_left {
width: 542px;
float: left;
border-top: 4px #FFF solid;
}
#right_right {
width: 230px;
float: left; color: #FFF; 
}
h3 {
background: transparent;
width: 123px;
height: 34px;
color:#FFF!important;
font-size: 10px;
}
.h3left {
background: url('http://aijt.nl/uploads/emplix/stylish/buttonleft.png')!important;
padding: 10px 100px 0 7px;
}
.h3right {
background: url('http://aijt.nl/uploads/emplix/stylish/buttonright.png')!important;
padding: 10px 7px 0 100px;
}
h2 {
font-size: 18px;
color: #717171;
padding: 5px 12px 5px 12px;
background-color: rgba(0, 0, 0, 0.02);
border-bottom: 1px solid #E7E7E7;
margin-top: 0;
text-transform: none;
font-weight: normal;
}
.navigation {
width: 222px;
margin-left:4px;
}
.navigation li {
width: 222px;
}
#right_right table, #right_right iframe, #left iframe, #left table {
width: 220px;
margin-left: 4px;
  background-color:transparent!important;
}
#right_left table, #right_left iframe, #right_left table td, #right_left table tr{  background-color:transparent!important;}
#right_right table td, #left table td{
  background-color:transparent!important;
  border-bottom: 1px solid #090909
}
#right_left iframe {
border: none;
width: 100%;
}
#shoutbox {
width: 218px!important;
margin-left: 6px;
}
#left object, #left object embed, #left embed { width: 220px!important; height: 220px!important;}
#left div embed { margin-left: 4px; }
.title {
color: #FFF;
background: #ff7701; /* Old browsers */
background: -moz-linear-gradient(-45deg,  #ff7701 0%, #ff4200 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, right bottom, color-stop(0%,#ff7701), color-stop(100%,#ff4200)); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(-45deg,  #ff7701 0%,#ff4200 100%); /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(-45deg,  #ff7701 0%,#ff4200 100%); /* Opera 11.10+ */
background: -ms-linear-gradient(-45deg,  #ff7701 0%,#ff4200 100%); /* IE10+ */
background: linear-gradient(135deg,  #ff7701 0%,#ff4200 100%); /* W3C */
}
.category_big, .category_big a, a, a:visited, a:active { color: #ff7701; }
a:hover { color: #ff7701; text-decoration: underline; }
  .newshead {
padding: 11px 0 0 3px;
}
  a.titlelink, a.titlelink:visited, a.titlelink:active { color: #FFF!important; font-weight: normal; font-size: 14px; }
a.titlelink:hover { text-decoration: underline; font-weight: normal; }
#footer {
background: url('http://aijt.nl/uploads/emplix/stylish/footer.png');
width: 976px;
height: 73px;
padding: 50px 20px 0 20px;
color: #ff843e!important;
}
#footer a.white, #footer a.white:visited, #footer a.white:active { color: #ff843e!important;}
.footer, #left hr {
display: none;
}
}
