css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

    .global-nav-inner, .header-inner, .list-link, .new-tweets-bar, .modal-content, .modal-header, .follow-bar, .stats{
        background-color : /*[[darkBackground]]*/ !important;
    }
    .content-header, .list-link, .flex-module, .original-tweet-container, .stream-item, .new-tweets-bar, .modal-header, .tweet, .profile-social-proof, .stats, .DashboardProfileCard, .DashboardProfileCard-bg, .header-inner{
        border-color : /*[[darkBorder]]*/ !important;
    }
    .stream-item, .original-tweet-container, .tweet-actions, .recent-tweets,  .permalink, .tweet, .ProfileTweet, .TwitterPhoto-media, .DashboardProfileCard-avatarLink{
        background-color : /*[[lessDarkBackground]]*/ !important;
    }
    .flex-module, .WhoToFollow, .Trends, .DashboardProfileCard{
        background-color : #444 !important;
    }
    
    .tweet-actions{
        box-shadow : none !important;
    }
    .tweet:hover, .tweet-actions:hover{
        background-color : /*[[tweetHover]]*/ !important;
    }
    
    /* Handling the 'fancy' new profile page */
    .ProfilePage{
        background-color : #333 !important;        
    }
    .ProfileCanopy-navBar, .ProfileNav-item--userActions{
        background-color : #222;
        border-color : #555;
    }
    .ProfileHeaderCard-bio, .ProfileHeaderCard-name, .ProfileHeaderCard-locationText, .ProfileHeaderCard-joinDateText,  .ProfileNav-item.is-active .ProfileNav-value, .DashboardProfileCard-name, .StreamToggler-link.is-active{
        color : #888;
    }
    .ProfileCard, .ProfileCard-bg{
        background-color : #666;
        border-color : #555;
    }
    .ProfileCard-bio{
        color : #AAA;
    }
    .TwitterPhoto-media{
        border-color : #555;
    }

}
