css:
@-moz-document domain("youtube.com") {

.site-center-aligned #yt-masthead-container {

    background: #201F1F;

}

canvas, caption, center, cite, code, dd, del, dfn, div, dl, dt, em, embed, fieldset, font, form, h1, h2, h3, h4, h5, h6, hr, html, i, iframe, img, ins, kbd, label, legend, li, menu, object, ol, p, pre, q, s, samp, small, span, strike, strong, sub, sup, table, tbody, td, tfoot, th, thead, tr, tt, u, ul, var {

    background: #201F1F;

}

.guide-item.guide-item-selected, .guide-item.guide-item-selected .yt-deemphasized-text, .guide-item.guide-item-selected:hover .yt-deemphasized-text, .guide-collection-item .guide-item.guide-item-selected {

    background: rgba(0, 0, 0, 0);

}

.guide-item.guide-item-selected:hoverm, .guide-item.guide-item-selected:hover, .guide-flyout-trigger.on-hover, .guide-item:hover, .guide-item:hover .yt-deemphasized-text, .guide-collection-item:hover .guide-item {

    background: #444;

    color: #fff;

}

#masthead-appbar-container {

    background-color: #201F1F;

    border-bottom: none;

}

a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading {

    color: #9C9C9C;

    font-weight: bold;

}

.branded-page-module-title a {

    color: #9C9C9C;

}

/* masthead */

#yt-masthead-container {

    border-bottom:1px solid #535353;

}

#masthead-expanded-container {

    background-color: #201F1F;

}

.site-center-aligned #masthead-expanded-container {

    background: none;

}

#masthead-expanded .masthead-expanded-menu-header {

    color: #999;

}

#masthead-expanded-menu-account-info p {

    color: #999;

}

.masthead-search-terms-border {

    border:1px solid #535353;

}

/* feed content */

.branded-page-v2-primary-col .branded-page-box {

    border-bottom: 1px solid #535353;

}

/* left side bar */

.site-center-aligned .yt-card {

    background-color: #201F1F;

}

.branded-page-v2-secondary-col .branded-page-related-channels-see-more a {

    color: #999999;

}

/* general button rules */

.yt-uix-button-content, .yt-uix-button-icon-wrapper, .yt-uix-button-content span {

    background-color: rgba(0, 0, 0, 0);

    color: #666;

}

.yt-uix-button-text, .yt-uix-button-text[disabled], .yt-uix-button-content span {

    border: none;

    color: #999999;

}

.yt-uix-button-default:hover, .yt-uix-button-text:hover {

    border-color: none;

    background: #535353;

}

.yt-uix-button-default, .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus {

    background: #444444;

    border:1px solid #535353;

}

/* 3 line bar button logo*/

.exp-appbar-onebar #masthead-positioner:hover #appbar-guide-button {

    border-color: #d3d3d3;

    background: #201F1F;

}

/* subscribe button */

.yt-uix-button-subscribe-unbranded, .yt-uix-button-subscribe-unbranded[disabled], .yt-uix-button-subscribe-unbranded[disabled]:hover, .yt-uix-button-subscribe-unbranded[disabled]:active, .yt-uix-button-subscribe-unbranded[disabled]:focus, .yt-uix-button-subscribed-branded, .yt-uix-button-subscribed-branded[disabled], .yt-uix-button-subscribed-branded[disabled]:hover, .yt-uix-button-subscribed-branded[disabled]:active, .yt-uix-button-subscribed-branded[disabled]:focus, .yt-uix-button-subscribed-unbranded, .yt-uix-button-subscribed-unbranded[disabled], .yt-uix-button-subscribed-unbranded[disabled]:hover, .yt-uix-button-subscribed-unbranded[disabled]:active, .yt-uix-button-subscribed-unbranded[disabled]:focus {

    background-image: -moz-linear-gradient(bottom, #313131 0, #202020 100%);

    background-image: -ms-linear-gradient(bottom, #313131 0, #202020 100%);

    background-image: -o-linear-gradient(bottom, #313131 0, #202020 100%);

    background-image: -webkit-linear-gradient(bottom, #f6f6f6 0, #202020 100%);

    background-image: linear-gradient(to top, #313131 0, #202020 100%);

    /*filter: progid:DXImageTransform.Microsoft.Gradient(GradientType=0, StartColorStr=#202020, EndColorStr=#313131);*/

}

/* Next button */

.compact-shelf .yt-uix-button-shelf-slider-pager {

    background: #8F8F8F !important;

}

.dashboard-widget-promos .yt-uix-slider-next.yt-uix-button-default, .dashboard-widget-promos .yt-uix-slider-prev.yt-uix-button-default {

    border: 1px solid #535353;

    background: #8F8F8F;

}

/* input type */

input, input[type="password"], input[type="search"], isindex {

    background: #201f1f;

}

/* watch page */

#watch7-headline, #watch7-user-header {

    background: #201F1F;

}

.yt-uix-expander-head, #watch7-user-header .yt-user-name, #watch7-user-header .yt-user-videos, .watch-view-count {

    color: #999;

}

.site-center-aligned #watch7-action-buttons, .site-center-aligned #watch7-action-panels, .site-center-aligned #watch-discussion {

    border-left: 1px solid #535353;

}

.site-center-aligned #watch-discussion {

    border-bottom: 1px solid #535353;

}

#watch7-action-buttons {

    border: 1px solid #535353;

}

#watch7-sidebar {

    background: #201F1F;

}

.video-list .video-list-item .title {

    color: #999;

}

.video-list-item a:visited .title, #watch7-sidebar .video-list-item a:visited .title {

    color: #593181;

}

#watch7-action-panels {

    border-left: 1px solid #535353;

    border-right: 1px solid #535353;

}

#watch-description {

    line-height: 17px;

    color: #999;

}

.metadata-inline {

    background: #201F1F;

}

#watch7-action-panels #watch7-action-panel-footer {

    background: #201F1F;

}

#watch-discussion {

    border: 1px solid #535353;

}

/* event thingy */

.not-exp-new-metadata-style .yt-lockup .yt-lockup-meta a {

    color: inherit;

}

/* channel page */

.branded-page-v2-primary-col .branded-page-box, .branded-page-v2-primary-col .branded-page-box:last-child.video-player-view-component, .site-center-aligned .branded-page-v2-subnav-container, #c4-header-bg-container {

    border-bottom: 1px solid #535353 !important;

}

.about-network-stat, .about-subscriptions, .other-channels-module, .package-module {

    border-top: 1px solid #535353;

}

.yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-unbranded-horizontal {

    background-color: #201F1F;

}

.yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-branded-vertical {

    border: 1px solid #535353;

}

/* dropdown menu */

.yt-uix-button-menu {

    background-color: #201F1F;

}

.yt-uix-button-menu .yt-uix-button-menu-item {

    color: #999;

}

/* users pages */

.creator-sidebar-item-content {

    background: none;

}

.vm-list-view .vm-video-list li {

    background: #201f1f !important;

}

.dashboard-widget-todos .todo-item, .dashboard-widget-todos .todo-description {

    border:none;

}

#non-appbar-vm-video-actions-bar .vm-video-actions-inner, #dashboard-header, .dashboard-widget-todos .todo-item, .site-center-aligned #creator-sidebar .creator-sidebar-section, .site-center-aligned #creator-sidebar .creator-sidebar-section.selected, #c4-header-bg-container, .channel-header .secondary-header-contents, .branded-page-v2-body #channel-feed-post-form, .site-center-aligned .branded-page-v2-subnav-container, .feed-item-container.legacy-style .feed-item-main {

    border-bottom: 1px solid #535353 !important;

}

.yt-uix-form-input-radio-element, .yt-uix-form-input-checkbox-element, .yt-uix-button-menu, .dashboard-widget.notification, .dashboard-widget .dashboard-widget-content, .dashboard-widget .dashboard-widget-config, .feed-item-content-wrapper.playlist-promo, .feed-item-content-wrapper.channel-lockup {

    border: 1px solid #535353 !important;

}

.feed-item-dismissal {

    border-top: 1px solid #535353 !important;

    border-bottom: 1px solid #535353 !important;

}

#vm-page-subheader h3, #creator-sidebar .creator-sidebar-channel-link a, #creator-sidebar .creator-sidebar-channel-link a:hover, #creator-sidebar .creator-sidebar-section a, .site-center-aligned #creator-sidebar>.creator-sidebar-single-section.selected>h3 a, .site-center-aligned #creator-sidebar>.creator-sidebar-section.selected>h3 a:hover {

    color: #999999;

}

.yt-uix-form-input-checkbox-container input:checked+.yt-uix-form-input-checkbox-element {

    background: no-repeat url(http://s.ytimg.com/yts/imgbin/www-hitchhiker-vfl3_86OG.webp) -42px -232px;

}

.yt-uix-form-input-select, .yt-uix-form-input-text, .yt-uix-form-input-textarea {

    border: 1px solid #535353;

    color: #999;

}

#yt-admin-content, .account-container, .channel-ad-fan-finder-banner, #dashboard-header, .channel-header .secondary-header-contents {

    background: #201f1f !important;

}

#dashboard-header h1, #dashboard-header .dashboard-stat-value {

    color: #999 !important;

    text-shadow: 0 1px 0 #5F5F5F !important;

}

#dashboard-header h2 {

    text-shadow: 0 1px 0 #5F5F5F !important;

    color: #468aca;

}

.guided-help-box {

    color: #999 !important;

    border: 1px solid #535353;

    background: #201f1f;

}

.secondary-header-contents .nav-text, .channel-header .branded-page-header-title .branded-page-header-title-link, .c4-checklist-module .checklist-item-title-text, .branded-page-module-title, a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading {

    color: #999 !important;

}

/* what to watch */

.feed-item-container.destination-moments-promo .feed-item-main, .yt-lockup-playlist-item  {

    border-bottom: 1px solid #535353 !important;

}

.feed-item-container.destination-moments-promo .feed-item-main {

    border-top: 1px solid #535353 !important;

}

.branded-page-module-title a:visited, .branded-page-module-title a, .feed-item-lockup-title h4, .feed-item-promo-heading h4 {
     
     color: #999 !important;
      
}
}

@-moz-document url-prefix("https://plus.googleapis.com"), url-prefix("https://apis.google.com"), regexp("https://plus.google.com/.*/widget/render/comments?.*"), regexp("https://plus.google.com/.*/notifications/frame?.*") {
/* comments */
 .yJa, .DJa, .mj, .E5, .e4, .BJa, .xN.d-y-r-c, .xN.d-y-r-c.d-y-r-c-eb {
    background-color: #201f1f !important;
}
.e4, .cp, .xN .d-y-r-c-ha, .Pga.d-A .d-A-B, .dn, .Aq, .jbgcdb {
    color: #999;
}
  
.Caa{
    display: none;
}

.r3 .r0 {
	background-color: #201f1f;
    background-image: -webkit-linear-gradient(bottom, #f6f6f6 0, #202020 100%);
    background-image: linear-gradient(to top, #313131 0, #202020 100%);
    color: #999;
    border: 1px solid #535353;
}
    
.E5, .e4, .d-y-r-c {
    border: 1px solid #535353;
}        
}
