@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("tvtropes.org") { 
 div#wikifoot, div#watchmsg, img[src="http://creativecommons.org/licenses/by-sa/3.0/"] {
	display: none !important}
 body, div, h1, h3, label {
	background-color: #000 !important;
	color: #DDD !important}
 a {
	color: #0F0 !important}
 span.spoiler {
	color: #222 !important}
 li.plus {
	background-color: #333 !important;}
 #wikileftpage h1, div.buttonframe {
	border: none !important}
 input, select {
	background-color: #060 !important;
	border: #060 !important;
	color: #000 !important} }

@-moz-document url-prefix("http://tvtropes.org/phpbb3/") {
 span {
	background-image: none !important}
 dl.icon, li.row, dd, blockquote {
	background-color: #060 !important;
	border: none !important;
	color: #FFF !important}
 span.spoiler {
	background-color: #000 !important}
}
