css:
@-moz-document url-prefix("https://twitter.com") {
/* 상단바 까맣게 */
.global-nav-inner{background-color:rgba(0,0,0,.7)}
.nav .active a{color:#eee}
.nav>li{color:#999}
body{background-position-y:46px !important}

/* 메시지바 까맣게 */
.message{background-color:rgba(37,37,37,.5)}
.message .message-text{color:#eee}
.message{padding:10px 20px;border-radius:6px} /* 메시지바 사이즈 줄이기 */
#message-drawer{margin-top:5px} /* 메시지바 상단바에서 떼기 */

/* 싱글 트윗 화면 글씨 작게 */
.permalink-tweet p{font-size:16pt;line-height:1.4}

/* Confirm 화면 시 배경화면 좀더 투명하게 */
.modal-overlay{opacity:.3}

/* 존잘님들의 클릭하면 보이는 그림들을 계속 감상하기 위해 */
.Gallery-content{background-color:rgba(0,0,0,.7)}

/* 알티알티 관글알티 안 보이게 */
.js-activity-retweeted_retweet, .js-activity-favorited_retweet{display:none}
}
