css:
@-moz-document domain("deviantart.com") {
.oh-eax h1 {
	overflow: hidden !important; 
}
.oh-hashover > h1 {
    overflow: hidden !important;
}
#da-h1 img {
  	visibility: hidden; 
}
#da-h1 {
  	position:absolute; top: 0px; left: 0px; 
  	overflow:visible;
    background-image: url('http://static.tumblr.com/dve3anh/E2pne7v9i/dlogo.png') !important;
  	background-size: contain !important;
    left:50px;
  	-webkit-animation:pulse 2s infinite ;
    animation:pulse 2s infinite;}

#da-h1:hover {-webkit-animation:pulsehover 2s forwards; 
    animation:pulsehover 2s forwards ;}
  
  @-webkit-keyframes pulse{
      0%{opacity:1}
     50%{opacity:0.5}
    100%{opacity:1;}
}

@-moz-keyframes pulse{
      0%{opacity:1;}
     50%{opacity:0.5;}
    100%{opacity:1;}
}
 
@-webkit-keyframes pulsehover{
     0%{opacity:1}
     
}
    
@-moz-keyframes pulsehover{
    0%{opacity:1}
    
}

.browse-facet ul li.facet-link-hidden {display: inline !important;}
    
    .browse-facet-toggle-hidden {display: none !important;}
    
    .browse-facet ul li.facet-link-hidden a {
        
        font-size: 11px !important;
        color: #337287 !important;
    }
    
    .browse-facet ul li.facet-link-hidden a:hover {color: #FFFFFF!important;}
   
    a[href="https://www.deviantart.com/checkout/?mx=premium&subpref=22870_0&point=wayback"] {display: none !important;}
