css:
@namespace url(http://www.w3.org/1999/xhtml);
/* Created by Robert "Anaerin" Johnston - anaerin@gmail.com */
@-moz-document domain("slashdot.org") {
    .comment-controls {
        position: inherit !important;
    }
    div.river-left {
    /* margin-right: 0 !important; */
    /* width: 100%; */
}
    span.current {
   float: left;
   color: white !important;
}
    div.container.split-right div.col-rail.rail-right.pull-right {
        position: absolute;
        top: 150px;
        right: 0px;
        z-index: 1;
        margin: 0 !important;
        float: none !important;
    }
    .comment-article header {
        padding-right: 0 !important;
    }
    div.elips:after, .layout-article:after, .comment-footer:after {
        clear: none !important;
    }
    .reply-preview-text {
        background-color: #f7f7f7;
    }
    .reply-preview-text a {
        font-size: 1em !important;
    }
div.pull-left {
    float: none !important;
}
    div.container {
   max-width: none !important;
   width: 100% !important;
}
    div.container.brandbar {
        z-index: 2;
    }
    div.container.split-right {
        background-image: none !important;
    }
    div.container.ts div.nav-wrap {
        background-image: none !important;
    }
article.comment-article {
   margin-top: 5px !important;
   margin-left: 15px !important;
   border: 1px solid #3E403F;
   border-collapse: collapse;
   border-top-left-radius: 10px;
   margin-right: 2px;
   padding: 0 !important;
}
i.com-toggle {
    display: none;
}
    aside.layout-rail:last-child {
        display: none;
    }
article.comment-article div.comment-body {
   padding: 10px 10px 0 10px;
    font-family: Helvetica, sans-serif !important;
}
article.comment-article h3.comment-title {
   color: white !important;
   background: linear-gradient(to bottom, #337D7C 0px, #006663 100%) repeat scroll 0% 0%;
   padding: 3px 5px 5px 10px;
   border-top-left-radius: 10px;
}
article.comment-article h4.avatar-tiny {
   padding: 0 5px 5px 10px;
   line-height: 1em !important;
}
article.comment-article h3.comment-title span {
   color: #cccccc !important;
}
div#commentlisting {
    z-index: 10;
}
footer.comment-footer {
   padding: 0 10px !important;
   border-bottom: none !important;
   font-family: Helvetica, sans-serif !important;
   /* Really? You can't define your font-family correctly? For shame, Chris Inman. */
}
article.comment-article header {
   margin-bottom: 0 !important;
   background-color: #ECEEEE;
   border-top-left-radius: 10px;
}
img.hero {
   width: auto !important;
   float: left;
   padding-right: 15px;
}
article.layout-article {
   clear: left;
}
    div#commentlisting {
        padding: 0 !important;
    }
}
