css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("volafile.io") {
    
*{

    border-color: rgb(30,40,60)!important;
    
}*

#overlay.visible {

    background: rgb(0,2,5);
}

#room_name, #room_name_container{

    text-align: center;

    background: rgb(0,10,25);

    color: rgb(220,220,220);

}

#header_row1.header_row {

    background: rgb(0,10,25);

}

.fa {
  
    color: rgb(220,220,220)!important;
    
}

.chat_message {

    background: rgb(15,20,30);

    color: rgb(220,220,220);

}

#chat_messages .chat_message.user a.username {

    color: rgb(120,140,200);

}

#chat_messages .chat_message.admin a.username {

    color: rgb(200,120,140);

}

#chat_messages .chat_message.admin {

    background: rgb(18,22,30);

}

#chat_messages .chat_message.rank a.username {

    color: rgb(80,100,180);

}

#chat_messages .chat_message a.username {

    color: rgb(180,190,200);

}

.chat_message.highlight, .chat_message.highlight.admin {

    background: rgb(25,35,50);

    border-color: rgb(200,120,140);

    border-width: 4px    

}

.chat_file {

    border-radius: 1px;

    background: rgb(30,35,50);
  
    border-color: rgb(30,35,50);
  
    color: rgb(120,140,200);
        
    text-align: center

}
    
.chat_file:hover {
        
        background: rgb(40,45,60)!important;
        
        color: rgb(140,160,200)
    }

.chat_file:visited {
        
        color: rgb(200,120,140)!important
    }    
    
#chat_frame a:visited { 
 
    color: rgb(200,120,140); 

}

.nano > .pane > .slider {

    border-radius: 1px;

    background: rgb(50,70,90);

}

.nano > .pane > .slider:hover {

    background: rgb(50,70,100)

}

.nano:hover > .pane, .pane.active, .pane.flashed {

    background: rgb(20,30,50);

    opacity: 1

}

.nano > .pane.active > .slider {

    background: rgb(120,140,170);

}

#chat_hbar, #header_row2.header_row {

    background: rgb(25,30,40);

    color: rgb(220,220,220);

}
 
.dropdown_item {
        
    background: rgb(25,30,40)
            
}
    
.dropdown:hover {
        
        background: rgb(25,30,40)
            
}
    
.dropdown_item:hover {
        
        background: rgb(0,10,25);
        
        color: rgb(120,140,200);
}

b, strong {
        
        color: rgb(220,220,220)
    
}    

textarea, input[type="text"], input[type="password"] {
        
        background: rgb(40,50,70)
                
    }    
    
.button, input[type="submit"] {
        
        border-radius: 1px;
        
        color: rgb(140,160,200)    
    }

.button:hover {
    
        background: rgb(40,45,55)!important;  
        
        
        
    }
    
#chat_name, #chat_input {

    background: rgb(10,15,25);

    color: rgb(220,220,220);
        
    border: none

}

a:hover {

    color: rgb(120,140,200);

}

.header_row_element, #header_row1, #header_row2 {
	
	border-color: transparent;
	
}

textarea.defaultValue {

    color: rgb(220,220,220);

}

#header_row_container a i.green-icon {

    color: rgb(200,120,140)!important;

}

#header_row_container a:hover i.green-icon {

    color: rgb(200,140,160)!important;

}

#header_row_container a:active i.green-icon {

    color: rgb(200,140,160)!important;

}

#search_input.defaultValue {

    background: rgb(40,50,70)!important;

    color: rgb(220,220,220);

}

#search_input {

    background: rgb(45,55,75)!important;

}

.toggle > i {
    
    color: rgb(220,220,220);
    
}

.toggle.enabled > i {

    color: rgb(120,140,200)!important;
    
}

.button, input[type="submit"] {

    background: rgb(25,30,40);

    color: rgb(120,140,200);

    margin-right: 10px;

}

#uploadButton {

    border-radius: 0px;

}

#uploadButton:hover {

    color: rgb(140,160,200);

    background: rgb(40,45,55)

}

#uploadButton:active {

    background: rgb(40,45,55);

    color: rgb(200,120,140)

}

#filter_reminder {

    background: rgb(30,35,55);

}

#filter_reminder:hover {

    background: rgb(40,45,65);

}

.file_uploading {

    background: rgb(30,40,60)!important;
    
}

#file_list.uneven .filelist_file:nth-child(2n+1) {
    background: rgb(15,20,30);
    color: rgb(220,220,220);
}

#file_list.even .filelist_file:nth-child(2n) {

    background: rgb(15,20,30);
    
    color: rgb(220,220,220);
    
}

.filelist_file {

    background: rgb(0,10,15);
    
    color: rgb(220,220,220);
    
}

.file_icon.filetype.music_playing {

    color: rgb(100,120,180)!important;
    
}

#gallery_title {
        
        color: rgb(120,140,200)
    }    
    
.gallery_button {
  
    border-radius: 1px;
  
    border-color: rgb(0,10,15)!important;
  
    color: rgb(120,140,200);
    
}

.ui_frame {
  
    background: rgb(0,10,15);
    
    border-color: rgb(50,70,90);
    
    border-radius: 1px;
    
}

a.file_name {
  
  color: rgb(220,220,220);
  
}

a.file_name:visited {
  
  color: rgb(200,120,140);
  
}

a.file_name:visited:hover {
  
  color: rgb(100,120,180);
  
}

a:hover {
  
  color: rgb(100,120,180);
  
}

.file_control_icon.clickable {
  
      color: rgb(220,220,220);
      
}
    
.file_control_icon.clickable:hover {
  
      color: rgb(200,120,140);
            
}
          
.file_icon {
        
      color: rgb(220,220,220);
              
}
    
.file_tag.tag_key_user{
        
        background: rgb(30,45,65);
        
        border-radius: 1px;
       
        color: rgb(220,220,220)
            
}
    
.file_tag.tag_key_user:hover {
        
        background: rgb(50,65,90);
        
}

.file_tag {
        
        background: rgb(20,30,50);
        
        border-radius: 1px;
            
        color: rgb(220,220,220);
}
    
.file_tag:hover {
        
        background: rgb(30,40,60)
    }    
    
html {

    background: rgb(0,10,15);
    
}

#main_logo {

    color: rgb(60,80,130);
    
}

#main_logo_suffix {

    color: rgb(100,120,180);
    
}

.button:hover {

    background: rgb(25,35,45);
    
}

.Button:active {

    background: rgb(40,45,60)!important;
    
}

#main_discover_button {

    background: rgb(0,10,25);
    
    color: rgb(200,120,140);
    
}

pre {
  
  color: rgb(220,220,220);
  
}
}
