@-moz-document domain("twitter.com") {
#page-outer, #page-container {
    margin: auto !important;
    padding: 0px !important;
    width: 100% !important;
    position: relative !important;
    top: 25px !important;
}
#page-outer .dashboard-left {
    position: fixed !important;
    top: 50px !important;
    left: 1em !important;
    bottom: auto !important;
    right: auto !important;
    width: 20em !important;
}
#page-outer .dashboard-right {
    position:fixed !important;
    top: auto !important;
    left: 1em !important;
    bottom: 0px !important;
    right: auto !important;
    width: 20em !important;
}
#page-outer #timeline[role='main'] {
    margin-right: 1em !important;
    margin-top: auto !important;
    margin-left: auto !important;
    margin-bottom: auto !important;
    padding: 0px !important;
    float: right !important;
    width: -khtml-calc(100% - 23em) !important;
    width: -moz-calc(100% - 23em) !important;
    width: -ms-calc(100% - 23em) !important;
    width: -o-calc(100% - 23em) !important;
    width: -webkit-calc(100% - 23em) !important;
    width: calc(100% - 23em) !important;
}
*[class*="Photo"] *[class*="badge"], *[class*="Media"] .Icon {
	display: none !important;
	visibility: hidden !important;
	width: 0px !important;
	height: 0px !important;
	max-width: 0px !important;
	max-height: 0px !important;
}
*[class*="Photo"] iframe, *[class*="Media"] iframe {
	width: 40em !important;
	height: 35em !important;
}
*[class*="Photo"], *[class*="Media"], *[class*="Photo"] *, *[class*="Media"] * {
    position: relative !important;
    margin: auto !important;
    padding: 0px !important;
    display: block !important;
    visibility: visible !important;
    overflow: visible !important;
    width: auto !important;
    height: auto !important;
    max-width: none !important;
    max-height: none !important;
    float: left !important;
    top: auto !important;
    bottom: auto !important;
    left: auto !important;
    right: auto !important;
}
.meta-tags-container, .media-tagging-block {
    margin: auto !important;
    padding: 0px !important;
    clear: both !important;
}
.AppContainer {
    padding: 0px !important;
    margin: auto !important;
    width: 100% !important;
    max-width: none !important;
}
.flex-module, .flex-module * {
	font-size: 10px !important;
	line-height: 9px !important;
}
}
