css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.italiafilms.tv") {


DIV.social_networks{display:none !important;}
HEADER.entry-header{display:none !important;}

P.summary{ background-color: #111111 !important; color: #eeeeee !important;}

aside.widget{ 
background-color: #221515 !important;
color: #ee88ff !important;
margin:2px !important;
padding:2px !important;
 }

div.widget-area{ 
background-color: #303030 !important;
color: #eeeeee !important;
border:3px solid !important;
border-color: #221515 !important;
 }

LI{ 
background-color: #303030 !important;
color: #eeeeee !important;
border:1px solid !important;
border-color: #3e3e3e !important;
 }

DIV{ 
background-color: #303030 !important;
color: #eeeeee !important;
 }
 
SPAN{ 
background-color: #303030 !important;
color: #eeeeee !important;
 }

ARTICLE{background-color: #303030 !important;}

A{ 
background-color: #303030 !important;
color: #ffffff !important;
 }

BODY{
background: #303030 !important;
border-color: #3e3e3e !important;
color: #8a8a8a !important;
}

H4{color: #ffffff !important;}

h4.widget-title{
text-align:center !important;
padding:5px !important;
margin:2px !important;
}

div.social_networks{visibility:hidden !important;}

div.owl-carousel{display:none !important;}

ul.sub-menu{ 
column-count:3 !important;
column-width:185px !important;
background-color: #222222 !important;
color: #eeeeee !important;
width:546px !important;
 }

ul.last_series{ 
background-color: #222222 !important;
color: #eeeeee !important;
margin:3px !important;
padding:4px !important;
 }

ul.last_series > li{ 
margin:2px 2px !important;
padding:7px !important;
}
a.tvseries_episode{
padding:4px !important;
}

span.tvseries_name{
padding:4px !important;
}

li.recentcomments{
padding:4px !important;
}

@-moz-document url-prefix(http://disqus.com/embed/comments)
{ 
div.textarea {background:#777!important}
div.post-actions {background:#888!important}
div#layout span.author{color:#eee!important}
p,strong,div.textarea,input
 {color:#eee!important;}
}

}
