css:
@-moz-document domain(torentz.3xforum.ro) {

BODY { background-color: #ddd }

TD {
	font: 10px Verdana, Arial, Helvetica, sans-serif;
	color: #333333
}

INPUT, SELECT {
	font: 10px Verdana, Arial, Helvetica, sans-serif;
	color: #333333
}

TEXTAREA {
	font: 10px Verdana, Arial, Helvetica, sans-serif;
	color: #333333
}

FORM { margin: 0 }

PRE {
	font-size: 11px;
	margin: 0
}

TABLE.punmain {
	border: none;
	width: 100%;
	background-color: #606060
}
TABLE.punplain {
	border: none;
	width: 100%
}
TABLE.punspacer {
	border: none;
	width: 100%
}

TR.punhead { background-color: #AAC9EB }
TR.puncon1 { background-color: #DEDFDF }
TR.puncon2 { background-color: #EEEEEE }
TR.puncon3 { background-color: #C0C0C0 }
TR.puntopic { height: 1.5em }

TD.punhead { color: #102945 }
TD.punheadcent {
	color: #102945;
	text-align: center
}
TD.puncon1 { background-color: #DEDFDF }
TD.puncon1cent {
	background-color: #DEDFDF;
	text-align: center
}
TD.puncon1right {
	background-color: #DEDFDF;
	text-align: right
}
TD.puncon2 { background-color: #EEEEEE }
TD.puncon2cent {
	background-color: #EEEEEE;
	text-align: center
}
TD.puncon3 { background-color: #C8C8C8 }
TD.puncent { text-align: center }
TD.punright { text-align: right }
TD.puntop { vertical-align: top }
TD.puntopright {
	text-align: right;
	vertical-align: top
}
TD.punquote {
	background-color: #F6F6F6;
	border: #606060;
	border-style: dashed;
	border-width: 1px
}

A:link, A:visited {
	text-decoration: none;
	color: #005CB1
}
A:link.punhot, A:visited.punhot { color: #C03000 }
A:link.punclosed, A:visited.punclosed { color: #888888 }
A:hover {
	text-decoration: none;
	color: #0099DD
}
A:hover.punhot { color: #F43E00 }
A:hover.punclosed { color: #AAAAAA }

IMG.punavatar {
	margin-top: 3px;
	margin-bottom: 3px
}

.puntext { font-size: 11px }
.punedited {
	font-size: 10px;
	font-style: italic;
}
.punsignature { font-size: 10px }
.punheadline {
	font-size: 12px;
	font-weight: bold;
}
.puntitle {
	font-size: 14px;
	font-weight: bold
}
.punhot { color: #C03000 }

}
