css:
@-moz-document domain("dashboard.bloglines.com"), domain("netvibes.com") {
/*
 * BLOGLINES/NETVIBES
 *
 * Other options:
 * http://userstyles.org/styles/87392/zenburn-for-netvibes-reader (dark background, still larger than I'd like)
 * http://userstyles.org/styles/97205/netvibes-new-reader-mods-jan-2014 (still larger than I'd like)
 * http://userstyles.org/styles/97346/netvibes-com (still larger than I'd like)
 */

.nv-ir-article-text {
  max-width: none !important;
}

.nv-item-renderer.feed,
.nv-item-renderer.nv-reader-item {
  padding: 0 5px !important;
}

.nv-item-renderer.feed {
  margin: 0 !important;
}

.nv-treeview-content .nv-treeview-row {
  padding-left: 24px !important;
}

.nv-treeview-row.hasUnread .nv-treeview-label {
  color: #fff;
}

.nv-treeview-row .nv-treeview-label {
  color: #999;
}

/* Even "Compact Mode" is 1.5. */
.nv-ir-article-text,
.nv-ir-article-text * {
  line-height: 1.25 !important;
}

.nv-ir-article-text p,
.nv-ir-article-text li {
  margin: 1em 0 !important;
}

/* item in a feed */
.nv-reader-item .item-header,
/* feed in the list */
.nv-treeview-section .nv-treeview-row,
.nv-treeview-feed {
  height: 20px !important;
}

.nv-treeview-unread {
  top: 3px !important;
  height: 14px !important;
  line-height: 14px !important;
}

.current .nv-treeview-unread {
  background: #444 !important;
}

/* item in a feed */
.nv-reader-item .item-header > *,
/* feed in the list */
.nv-treeview-row {
  line-height: 20px !important;
}

.nv-reader-item .item-feedinfo-favicon {
  margin-top: 0 !important;
}

.nv-treeview-section .nv-treeview-row .icon {
  top: 2px !important;
}

body .current > .nv-treeview-row, body.no-touch .current > .nv-treeview-row:hover, body .current > .nv-treeview-row.hasUnread {
  background: #000 !important;
}

.nv-ir-article-text table {
  border-spacing: 2px !important;
  border-collapse: separate !important;
}

.nv-ir-article-text tr {
  display: table !important;
}

.nv-ir-article-text tr {
  display: table-row !important;
}

.nv-ir-article-text td {
  display: table-cell !important;
}

.nv-ir-article-text ol > li {
  list-style-type: decimal !important;
}

.nv-ir-article-text ul > li {
  list-style-type: disc !important;
}

.nv-ir-article-text ol,
.nv-ir-article-text ul {
  padding-left: 40px;
}

.no-native-scrollbars::-webkit-scrollbar {
  display: block !important;
}

.no-native-scrollbars {
  right: 0 !important;
}

.nv-ir-article-text {
  font-size: 12px !important;
  font-family: Verdana, Geneva, Arial, Helvetica, sans-serif !important;
}

.nv-ir-article-text img {
  margin: 3px 6px !important;
}

.nv-ir-article-text br + br {
  display: inline !important;
}

blockquote {
  margin: 0 40px !important;
}

#footer-toggle {
  display: none;
}

.nv-reader-item.open .item-actions-wrapper {
  margin-top: 0 !important;
}

.nv-item-renderer .item-actions-wrapper {
  margin-top: -42px !important;
}

.item-actions-wrapper.is-sticky {
  display: none !important;
}
}
