css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slashdot.org") {

/* Or you can limit it to the beta urls */
/*
@-moz-document url-prefix("http://meta-beta.slashdot.org"),
               url-prefix("http://beta.slashdot.org") {
*/


.comment-title {
  font-weight: bold !important;
  }
  
.commentBody,
.comment-body, 
.comment-body p,
.elips p,
.quote {
  color:        rgb(54, 54, 54)   !important;     
  font-family:  Arial, sans-serif !important;
  font-size:    13px   !important;
  font-style:   normal !important;
  font-variant: normal !important;
  font-weight:  normal !important;
}
  
.quote {  
  border-left: 3px solid rgb(221, 221, 221) !important;    
  margin-top:13px !important;
  margin-bottom:13px !important;    
  padding-top:0px !important;
  padding-bottom:0px !important;        
}

.quote p {    
    }    
  
.comment-article.com-show {
  padding: 0px 0px 0px 15px !important;
  border-left:   1px solid #ccc !important;
  border-right:  1px solid #ccc !important;
  border-bottom: 1px solid #ccc !important;   
  border-top:     none !important;
  margin-bottom:  5px !important;
  margin-top:    15px !important;
  }    

.comment-article.com-show header {
      margin-left:-15px  !important;
      padding-right:0px  !important;
      margin-bottom:20px !important;
    }

.comment-title span {
  padding-left: 10px !important;
  color: #fff !important;
  }

.comment-wrapper {
      padding-right:15px !important;  
  }
  
.comment-footer {
  padding-bottom:0px !important;  
  }

.com-toggle {
  display:none;
  }

.river-left {
  margin-right: 0px !important;
}
  
.split-right {
   background-image:none !important;
   background-color:#ffffff !important;
}
    
.pull-right {
  display: none !important;
}

    
#commentlisting h3 {
    font-size:    13px   !important;
  
    background: rgb(0,66,66) !important;
    background-image: -webkit-gradient(linear, 0% 0%, 0% 100%, from(rgb(0,102,102)), to(rgb(0,66,66))) !important;
    background-image: -moz-linear-gradient(100% 100% 90deg,rgb(0,66,66), rgb(0,102,102) 100%) !important;
    background-image: -o-linear-gradient(top, rgb(0,102,102), rgb(0,66,66) 100%) !important;
    border-radius: 10px 0 0 0 !important;
    -moz-border-radius: 10px 0 0 0 !important;
    -webkit-border-radius: 10px 0 0 0 !important;
    -o-border-radius: 10px 0 0 0 !important;
    color: rgb(255,255,255) !important;
  
    font-weight: bold !important;
    font-family: arial, serif !important;
    padding: 4px 20px !important;
  }

#commentlisting h4 {
    font-size:    13px   !important;  
  
    background: none repeat scroll 0 0 #E1E1E1;
    color: #4D4D4D !important;;
    font-size: 0.75em !important;
    line-height: 1.2em !important;
    margin: 0 !important;
    padding: 6px 20px !important;
    text-indent: 0 !important;
}

#commentlisting h4 a.author {
  color:            rgb(0,102,102) !important;
}

/* hide floating nav bar once scrolled down*/
.branddark.sticky.fixed {
        z-index:-1000;
      }

}
