css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.italiafilms.tv") {


DIV.social_networks{display:none !important;
background-color: #FF7F00 !important; color: #A5A5A5 !important;
border:2px solid !important;
border-color: #000000 !important;
}
HEADER.entry-header{
background-color: #000000 !important; color: #A5A5A5 !important;
border:2px solid !important;
border-color: #000000 !important;
}

P.summary{ background-color: #2D2D2D !important; color: #A5A5A5 !important;}

aside.widget{ 
background-color: #000000 !important;
color: #D72D03 !important;
 }

div.widget-area{ 
background-color: #FF7F00 !important;
color: #2D2D2D !important;
border:3px solid !important;
border-color: #000000 !important;
 }

LI{ 
background-color: #000000 !important;
color: #A5A5A5 !important;
border:3px solid !important;
border-color: #FF7F00 !important;
 }

DIV{ 
background-color: #2D2D2D !important;
color: #A5A5A5 !important;
 }
 
SPAN{ 
background-color: #000000 !important;
color: #A5A5A5 !important;
 }

ARTICLE{
background-color: #2D2D2D !important;
color: #A5A5A5 !important;
 }

A{ 
background-color: #2D2D2D !important;
color: #ffffff !important;
 }

BODY{
background: #000000 !important;
border-color: #FF7F00 !important;
color: #A5A5A5 !important;
}

H4{color: #A5A5A5 !important;}

div.social_networks{visibility:visible !important;
background: #000000 !important;
color: #A5A5A5 !important;
}

div.owl-carousel{display:none !important;}

ul.sub-menu{ 
column-count:4 !important;
column-width:150px !important;
background-color: #2D2D2D !important;
color: #A5A5A5 !important;
width:750px !important;
 }

@-moz-document url-prefix(http://disqus.com/embed/comments)
{ 
div.textarea {background:#777!important}
div.post-actions {background:#888!important}
div#layout span.author{color:#eee!important}
p,strong,div.textarea,input
 {color:#eee!important;}
}

}
