/*__ Red Gradient _________________________________________________________________________________________*/

#js-gdpr-consent-banner,
#herobox-mini,
#herobox.alternate,
.answer-help-background,
.bg-black-700,
.comment-help,
.nav-links .youarehere .nav-links--link,
.question-status,
