    background: linear-gradient(to bottom, #300202 0%, #000 100%)!important;
    border-bottom: 1px solid #000!important;
    border-radius: 22px!important;
}

ul.comments-list .comment:hover {
    background: linear-gradient(to bottom, #300202 0%, #000 100%)!important;
    border-bottom: 1px solid #000!important;
}

/*__ Blue Gradient __________________________________________________________________________________*/

.bounty-indicator,
.grippie,
.module.community-bulletin {
    background: linear-gradient(to bottom, #083377, #000 100%)!important;
    border-bottom: 1px solid #000!important;
    border-radius: 22px!important;
}

/*__ Green Gradient __________________________________________________________________________________*/

#tabs a.youarehere,
.answered-accepted,
.filter a.active,
.filter a.youarehere,
