css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rust-lang.org") {
    body, .content, .sidebar, .sidebar .block{
        background-color:#202025;
        color:#888;
    }
    blockquote {
     background-color:#999; 
     border-radius: 10px;
    }
    pre.notrust{
     background-color:black;   
    }

    a {
        color:teal;   
    }

    a:hover{
        color:red!important;
        text-decoration:underline!important;
    }

    h1.title, h1 a {
        color: #789!important;   
    }

    h2.title, h2 a {
        color: #789!important;   
    }

    .cm-s-default, .docblock pre{
        background-color:#aaa;   
    }

    .docblock pre {
        background-color:black;
        border-radius: 5px;   
    }

    .docblock pre code {
        color:#134;
    }

    .docblock code {
        color:#346;
    }

    .docblock h1 {
        color:#aaa!important;
    }

    .search-input {
        background-color:#555;
        border:none;
        padding: .3em 0 .3em .4em;
    }

    img.img-responsive, .sidebar img {
        background-color:#820;   
        border-radius:10px;
    }

    pre.trait, pre.struct, pre.enum, pre.fn{
        background-color:black;
        border-radius:10px;
    }

    pre.trait {
        border-color: rgba(252, 174, 43,.7) !important;
    }

    pre.struct {     
        border-color: rgba(229, 55, 0, .7) !important;
    }

    pre.enum {
        border-color:rgba(147, 188, 153, .7) !important;
    }

    pre.fn {
        border-color: rgba(162, 119, 127, .7) !important;
    }

    p code, li code {
        background-color:black;
        padding:0 3px 0 3px;
        border-radius:4px;
    }

    a.struct, .block.struct h2 {
        color: rgba(229, 55, 0, .7) !important;
    }

    a.struct.current{
        color: rgba(229, 55, 0, 1) !important;
    }

    a.trait, .block.trait h2 {
        color: rgba(252, 174, 43,.7) !important;
    }

    a.trait.current {
        color: rgba(252, 174, 43, 1) !important;
    }

    a.mod, .block.mod h2 {
        color: rgb(128, 159, 199) !important;   
    }

    a.fn, .block.fn h2 {
        color: rgba(162, 119, 127, .7);
    }

    a.fn.current{
        color: rgba(162, 119, 127, 1);
    }
    
    .content.mod h2{       
        color: rgb(128, 159, 199) !important;  
    }
    pre.rust, pre.line-numbers {
     background-color:black;   
    }
    pre.line-numbers .line-highlighted {            
        background-color:#C67E2D;
        color:black;
    }
    
    span.white-sticker, a.hash.white-sticker {
     background-color:black;
     border: 1px solid #733;
    }
}
