@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nq.cba.pl") {
    body                {   background-color: black !important;
                            margin-top: -25px !important;
                        }
    
    div#reklamacba,
    div#dsz_sb_advert,
    div[id*=adKontekst],
    html body table#main.outer-border tbody tr td table tbody tr td.main-bg > :last-child,
    div.cbalink
                        {   display: none !important;}
    
    td.full-header      {   /*[[wersja]]*/
                            height: 10px !important;
                            background-color: black !important;
                            z-index: -1 !important;
                            
                            
                        }
                                                
    td.side-body form textarea.textbox 
                        {   width: 69% !important;
                            display: block;
                            margin-left: auto !important;
                            margin-right: auto !important;
                        }    
                        
    table tbody tr td.main-bg  td.scapmain      
                        {   display: block;
                            margin-left: auto !important;
                            margin-right: auto !important;
                            width: 141px !important;
                        }
                        
    html body table#main.outer-border tbody tr td table tbody tr td.main-bg table.border tbody tr td.side-body form > div,
    html body table#main.outer-border tbody tr td table tbody tr td.main-bg table.border tbody tr td.side-body form > :last-child
                        {   display: block;
                            margin-left: auto !important;
                            margin-right: auto !important;
                        }
                        
    html body table#main.outer-border tbody tr td table tbody tr td.main-bg table.border tbody tr td.side-body > div
                        {   display: block;
                            margin-left: 10% !important;
                            margin-right: 10% !important;
                        }
    html body table#main.outer-border tbody tr td table tbody tr td.main-bg table.border tbody tr td.side-body > div.shoutbox
                        {   border-bottom: 1px solid #018BC1 !important;
                            white-space:pre-wrap;
                            padding-bottom: 10px !important;
                        }
    
                        
    html body table#main.outer-border tbody tr td table tbody tr > td.sub-header
                        {   display: block;
                            margin-left: 250px !important;
                            margin-right: 250px !important;
                        }
                        
    html body table#main.outer-border tbody tr td table tbody tr td.side-border-left > :first-child,
    html body table#main.outer-border tbody tr td table tbody tr td.side-border-right > :first-child
                        {   margin-top: -260px !important;                            
                        }
        
}
