css:
@-moz-document domain(9gag.com)
{

h1,h2,h3,h4,h5,h6,a,span,p,.content
  {
    color: #ddd !important;
  }

  body
  {
    background: #222 !important;
  }

  #sidebar-content
  {
    display: none;
  }

  h1
  {
    color: #ddd !important;
  }

  #jsid-sticky-button
  {
    display: none;
  }

  .post-view img
  {
    width: 700px !important;
  }

  .share.right
  {
    display: none;
  }

  .post-tag a
  {
    color: #ddd !important;
  }

  .stealthy-scroll-container section:nth-child(2)
  {
    display: none;
  }

  .footer
  {
    display: none;
  }

  .nav a
  {
    color: #ddd !important;
  }

  .CS3
  {
    background: #222 !important;
  }

  .post-afterbar-a.in-post-top
  {
    background: #222;
  }

  .image-container img
  {
    width: auto !important;
  }
.video-post, .youtube-post{
	overflow:visible;
}

#top-nav{
    background:#000;
}

.drawer-container{
    background:#222;
}
.comment-list-item__text{
	color:#888;
}

.image-post.post-view img{
    padding:0 !important;
}

.sticky-navbar{
    display:none !important;
}
.comment-list-item:hover{
    background:#333 !important;
}

.inline-ad-container{
    display:none;
}

.billboard{
display:none;
}
#container{
    width:700px;
}
}
