css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://userscripts.org/scripts/review/)
,url-prefix(https://userscripts.org/scripts/review/)
,url-prefix(http://userscripts.org:8080/scripts/review/)
,url-prefix(https://userscripts.org:8080/scripts/review/)
,url-prefix(http://userscripts-mirror.org/scripts/review/)
,url-prefix(https://userscripts-mirror.org/scripts/review/)
{

pre.sh_sourceCode {
 background-color: #293134 !important;
 color: #E0E2E4 !important;
 line-height: 1.1em !important;
 font-size: 1em !important;
 white-space: pre-wrap !important; word-wrap: break-word!important;
}

pre.sh_sourceCode .sh_keyword {
  color: #93C763 !important;
}

pre.sh_sourceCode .sh_type {
  color: #00ff00;
}

pre.sh_sourceCode .sh_string {
  color: #EC7600 !important;
}

pre.sh_sourceCode .sh_regexp {
  color: #D39745 !important;
}

pre.sh_sourceCode .sh_specialchar {
  color: #ff22ff;
}

pre.sh_sourceCode .sh_comment {
  color: #818E96 !important;
}

pre.sh_sourceCode .sh_number {
  color: #FFCD22 !important;
}

pre.sh_sourceCode .sh_preproc {
  color: #ff22ff;
}

pre.sh_sourceCode .sh_function {
  color: #55CCFF !important;
}

pre.sh_sourceCode .sh_url {
  color: #a75 !important;
}

pre.sh_sourceCode .sh_date {
  color: #B26818;
}

pre.sh_sourceCode .sh_time {
  color: #B26818;
}

pre.sh_sourceCode .sh_file {
  color: #B26818;
}

pre.sh_sourceCode .sh_ip {
  color: #ff0000;
}

pre.sh_sourceCode .sh_name {
  color: #ff0000;
}

pre.sh_sourceCode .sh_variable {
  color: #B26818;
}

pre.sh_sourceCode .sh_oldfile {
  color: #ff22ff;
}

pre.sh_sourceCode .sh_newfile {
  color: #ff0000;
}

pre.sh_sourceCode .sh_difflines {
  color: #B26818;
}

pre.sh_sourceCode .sh_selector {
  color: #B26818;
}

pre.sh_sourceCode .sh_property {
  color: #B26818;
}

pre.sh_sourceCode .sh_value {
  color: #ff0000;
}

}
