@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("outlook.live.com") {
 /* Hide the side adds, hide boomering add*/
  #owaadbar, ._n_p, ._exn_u {display:none;}
  #primaryContainer > div:nth-child(4) {
    width: 100%!important;;
  }
  
  .ms-bg-color-white {
    background-color: #DFE0DF;
  }
  
  .ms-bg-color-themeLighterAlt, .ms-bgc-tlra, .ms-bg-color-themeLighterAlt-hover:hover, .ms-bg-color-themeLighterAlt-before::before, .ms-bgc-tlra-h:hover, .ms-bgc-tlra-b::before {
    background-color: #DFE0DF;
  }
  
  .listItemDefaultBackground, ._rp_15 {
    background-color: #F4F4F4;
  }
  
  body {
    background-color: #F4F4F4;
    position: absolute !important;
  }

  /* fix middle top panal size */
  ._n_a1, ._n_51 ._n_m1, ._n_u1 {
    top: 0px!important;
    background-color: rgb(244, 244, 244);
  }
  /* fix left pane size */
  .folderPaneGradient {top:40px!important;}
  .treeHeaderContainer {padding-top: 10px;}
  ._n_K4 {bottom: 0 !important;}
}
