/*********************************************
@name      Facebook Blue Theme
@namespace Facebook.com
@include   facebook.com/*
@author    Matúš Paulovič (c)
@version   1.0.1
*********************************************/

@-moz-document domain("facebook.com") 
{

/***********************************************************************
NÁZOV: Facebook Blue Theme
AUTOR: Matúš Paulovič (c)
VERZIA: 1.0.1
KONTAKT: Matúš Matthew Paulovič
***********************************************************************/
#blueBar 
{
background-color: #000066;
border-bottom: 1px solid #330033;
}

._50kd ._kso, ._50kd ._kso:before 
{
background-image: -webkit-linear-gradient(bottom, #99FF99, #fff);
}

._kso, ._kso:before 
{
background-image: -webkit-linear-gradient(bottom, #FFDD88, #e7f1fe);
}

.uiSideNav .selectedItem .item, .uiSideNav .selectedItem .item:hover, .uiSideNav ul .selectedItem .subitem, .uiSideNav ul .selectedItem .subitem:hover 
{
background-color: #FFFF66;
}
        
.fbJewel .jewelCount span 
{
background-color: #FF0066;
border: #FF0066;
border-bottom: 1px solid #FF0066;
border-top: 1px solid #FF0066;
}
        
.uiSideNavCount 
{
background-color: #CCBBEE;
}

}
