css:
@-moz-document domain("userstyles.org") {

/* 
 * Name:    Userstyles.org - Nicer and cleaner look
 * Title:   (:
 * Author:  Crystallis
 * Version: 0.1
 *
 */

 
body {
font-family:Verdana,sans-serif;
font-size:smaller;
max-width:980px ! important;
margin-left:auto ! important;
margin-right:auto ! important;
background: #f1f1f1 ! important;
color: #333333 ! important;
}
pre {
border: 1px dashed #c7c7c7 ! important;
background-color: #e1e1e1 ! important;
padding: 10px;
overflow: auto;
}
a {
color: #2793e6 ! important;
text-decoration: none ! important;
}
a:hover {
color: #2793e6;
text-decoration: underline ! important;
}
#main-header {
font-family:Trebuchet MS;
background-color: #ffffff ! important;
padding:5px;
margin-bottom:10px;
margin-left:5px;
border-radius:5px;
border: 1px solid #e8e8e8 ! important;
}
#left-sidebar>* {
background-color:#e1e1e1 ! important;
border-radius:5px;
margin:5px;
padding:5px;
border: 1px solid #c7c7c7 ! important;
}
li[class="more"] {
font-size: smaller;
border-top: 1px solid #c7c7c7 ! important;
margin-top: 8px;
padding-top: 4px;
}
div[id="screenshots"] {
margin-bottom: 10px;
border: 1px solid #c7c7c7 ! important;
padding: 5px;
border-radius: 5px;
min-height: 100px;
background: #e1e1e1 ! important;
}
textarea {
-webkit-appearance: textarea;
background-color: #ffffff ! important;
border: 1px solid #dddddd ! important;
border-top: 1px solid #d5d5d5 ! important;
border-image-source: initial;
border-image-slice: initial;
border-image-width: initial;
border-image-outset: initial;
border-image-repeat: initial;
-webkit-rtl-ordering: logical;
-webkit-user-select: text;
flex-direction: column;
resize: auto;
cursor: auto;
padding: 2px;
white-space: pre-wrap;
word-wrap: break-word;
}
input[id*="style_short_description"],
input[id*="example-url"],
input[name*="new_screenshot_description_"],
input[name*="screenshot_description_"],
input[id*="style_pledgie_id"],
input[id*="search-terms"],
input[id*="user_login"],
input[id*="password"],
input[id*="password_confirmation"],
input[id*="user_name"],
input[id*="user_email"],
input[id*="user_homepage"],
input[type*="url"],
input[id*="user_paypal_email"],
input[id*="widget-title"],
input[id*="obsoleting-style-id"] {
-webkit-appearance: textfield;
padding: 2px;
background-color: #ffffff ! important;
border: 1px solid #dddddd ! important;
border-top: 1px solid #d5d5d5 ! important;
border-image-source: initial;
border-image-slice: initial;
border-image-width: initial;
border-image-outset: initial;
border-image-repeat: initial;
-webkit-rtl-ordering: logical;
-webkit-user-select: text;
cursor: auto;
}
input[id*="setting-"] {
border: 1px solid #dddddd ! important;
border-top: 1px solid #d5d5d5 ! important;
}
.notice {
background-color: #e1e1e1 ! important;
border: 1px dashed #c7c7c7 ! important;
text-align: center ! important;
}
@media(max-width:900px) {
#left-sidebar {
float:none ! important;
width:auto ! important;
margin-left:5px ! important;
margin-top:1em ! important;
}
#content-wrapper {
float:none ! important;
width:auto ! important;
}
#main-article {
margin-left:5px ! important;
margin-right:5px ! important;
}
#search-terms,#search-submit,#show-advanced-search {
float:none ! important;
}
#search-terms {
width:85% ! important;
}
#show-advanced-search {
text-align:left ! important;
}
nav form {
margin-bottom:.5em ! important;
}

}
@media(max-width:600px) {
.listing-left-info {
float:none ! important;
width:auto ! important;
}
.screenshot-thumbnail {
text-align:left ! important;
}
.style-brief-text {
margin-left:0 ! important;
}
.style-brief,#browse-middle-ad {
padding-top:15px ! important;
border-bottom:2px solid gray ! important;
padding-bottom:2em ! important;
}
.style-brief:last-of-type {
padding-bottom:5px ! important;
}
.style-brief-stats span {
display:inline ! important;
}
.style-brief-stats span::after {
content:"|" ! important;
}
.style-brief-stats span:last-child::after {
content:"" ! important;
}
#style-author-info {
margin-left:0 ! important;
}
#left-info {
float:none ! important;
width:auto ! important;
}
#main-style-info {
margin-left:0 ! important;
}
#info-tables th {
min-width:150px ! important;
}
#social {
height:auto ! important;
}
h1 {
font-size:1.25em ! important;
}
}
div[id="post-install-ad"] {
display: none ! important;
}
div[style="width: 300px; height: 262px;"] {
display: none ! important;
}


}
