@namespace url(http://www.w3.org/1999/xhtml);

/* Pour les différentes pages google */

@-moz-document  url-prefix(http://www.google),
                url-prefix(http://images.google),
                url-prefix(http://news.google),
                url-prefix(http://video.google),
                url-prefix(https://encrypted.google),
                url-prefix(https://www.google),
                url-prefix(https://accounts.google) {
	
    /* Changement du fond, utilisation de l'image des lions */
	body#gsr {
		background:url(http://image.noelshack.com/fichiers/2014/06/1391975496-wallpaper-1982571.jpg) !important;
		background-size: cover !important;
	}
    
	/* Pour que le fond soit blanc lors des recherches */
	.mw,.mdm {
		background-color: white;
	}
    
	/* Fond transparent pour le footer et couleur claire de police dans le footer et dans le header pour les 
       liens .fbar:pour la barre de footer #footer a:pour les liens du footer .gb_i a:pour les liens de 
    header */
    .fbar,#footer a,.gb_i a {
		color:rgb(230,230,230) !important;
		background: transparent;
	}
    
	/* Mettre logo google blanc*/
	#hplogo {
		background-image:url("https://upload.wikimedia.org/wikipedia/commons/d/d5/Transparent_google_logo.png") !important;
		background-repeat:no-repeat !important;
		background-size: 275px 95px !important;
	}
    
	/* cacher le texte "france" du logo */
	#hplogo div {
		display: none;
	}
    
	/* fond pour les liens du footer */
	.gb_i.gb_j a {
		background-color:rgba(0,0,0,0.5);
		padding: 0 10px;
	}
    
	/* Couleur du texte pour le menu des différents services Google */
	.gb_h {
		color: #151515;
	}
        /* Cacher les annonces Google */ 
        #prm{
                display: none;
        }
}
