css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("answers.unity3d.com") {
    body, textarea {
        background-color : #333 !important;
        color : #AAA !important;
    }
    a {
    	color : #3D7BD3 !important;
    }
    .background{
        background : none !important;
        background-color : #333 !important;
    }
    /***
     * Search bar
     ***/
    .gsc-input input.gsc-input{
        background : none #444 !important;
        color : #888 !important;
        
    }
    /***
     * Top Bar
     ***/
    nav.sub-nav{
        background-color : #111111 !important;
    }

    nav.sub-nav ul{
        background : linear-gradient(to bottom, #111111 0%, #2573A9 100%) repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
    }

    .tn-community .active-trail{
        background : linear-gradient(to bottom, #2573A9 0%, #111111 100%) repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
    }
    
    /***
     * Unity Answers Specific stuff
     ***/
    .accepted-answer{
        background : none #555855 !important;
        color : #CCC !important;
    }
    .accepted-answer a{
        color : #38E !important;
    }
    .comment{
        background : #333 !important;
    }
    .comment-text{
        color : #888 !important;
    }
    .wmd-preview{
        background-color : #222 !important;
    }
     /**
     * Code background
     **/
    .answer-body pre, .question-body pre, .comment-text pre{
       background-color : #444 !important;  
    }
    li.L1, li.L3, li.L5, li.L7, li.L9{
       background-color : #666 !important;
    }
    li.L0, li.L2, li.L4, li.L6, li.L8{
       background-color : #555 !important;
    }
    /***
     * Syntax highlighting
     ***/
    @media screen {
        .pln{ /*plain*/
           color: #CCC;  
        }
        .str { /*string*/
            color: #FF3B20;
        }
        .kwd { /*keyword*/
            color: #88E;
        }
        .com { /*comment*/
            color: #999;
        }
        .typ { /*type*/
            color: #DB2;
        }
        .lit { /*literals*/
            color: #2C2;
        }
        .pun, .opn, .clo { /* punctuation */
            color: #BBB;
        }
        .tag {
            color: #F0F;
        }
        .atn {
            color: #F0F;
        }
        .atv {
            color: #F0F;
        }
        .dec, .var {
            color: #F0F;
        }
        .fun {
            color: #F0F;
        }
    }
    /***
     * Side Bars
     ***/
    .boxC{
        background-color : #222 !important;
        border-color : #444 !important;
    }
    
    
}
