css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("3ix.org")
{

html, body, #top_menu, #content_left, #side_menu, td, .options, #footer, #nav li,
#under_head, .breadcrumb {
background:#000 !important;
}
.options a:hover, #top_menu li a:hover,
#nav li a:hover, .clientareatableterminated,
table.data tr.clientareatableterminated td {
background:red !important;
}
.clientareatablesuspended,
table.data tr.clientareatablesuspended td {
background:purple!important;
}
.clientareatablepending,
table.data tr.clientareatablepending td {
background:orange !important;
}
.clientareatableactive,
table.data tr.clientareatableactive td {
background:green !important;
}
a, p, b, td, h1, h2, h3, h4, font, .maintext, .menubot, .headertxt, .text1,
.menuboxtxt {
color:#FFF !important;
}
.menuboxtxt:hover {
text-decoration:underline !important;
}
}
