css:
@-moz-document domain("userstyles.org") {

/* 
 * Name:    Userstyles.org - Nicer and cleaner look
 * Title:   (:
 * Author:  Crystallis
 * Version: 0.2
 *
 */

 
body {
font-family:Verdana,sans-serif;
font-size:smaller;
max-width:980px ! important;
margin-left:auto ! important;
margin-right:auto ! important;
background: #141414 ! important;
color: #909090 ! important;
}
pre {
border: 1px dashed #3b3b3b ! important;
background-color: #202020 ! important;
padding: 10px;
overflow: auto;
}
a {
color: #2793e6 ! important;
text-decoration: none ! important;
}
a:hover {
color: #2793e6;
text-decoration: underline ! important;
text-shadow: 0px 0px 3px rgba(39,147,230,1) ! important;
}
#main-header {
font-family:Trebuchet MS;
background-color: #1e1e1e ! important;
padding:5px;
margin-bottom:10px;
margin-left:5px;
border-radius:5px;
border: 1px solid #1b1b1b ! important;
}
#left-sidebar>* {
background-color: #202020 ! important;
border-radius:5px;
margin:5px;
padding:5px;
border: 1px solid #1d1d1d ! important;
}
li[class="more"] {
font-size: smaller;
border-top: 1px solid #2b2b2b ! important;
margin-top: 8px;
padding-top: 4px;
}
div[id="screenshots"] {
margin-bottom: 10px;
border: 1px solid #1b1b1b ! important;
padding: 5px;
border-radius: 5px;
min-height: 100px;
background: #202020 ! important;
}
div[id="advanced-search"] {
display: none;
clear: both;
font-size: smaller;
border-bottom: 1px solid #2b2b2b ! important;
margin-bottom: 3px;
padding-bottom: 3px;
}
textarea {
-webkit-appearance: textarea;
background-color: #333333 ! important;
border: 1px solid #202020 ! important;
border-top: 1px solid #2b2b2b ! important;
border-image-source: initial;
border-image-slice: initial;
border-image-width: initial;
border-image-outset: initial;
border-image-repeat: initial;
-webkit-rtl-ordering: logical;
-webkit-user-select: text;
flex-direction: column;
resize: auto;
cursor: auto;
padding: 2px;
white-space: pre-wrap;
word-wrap: break-word;
color: #dddddd ! important;
}
input[id*="style_short_description"],
input[id*="example-url"],
input[name*="new_screenshot_description_"],
input[name*="screenshot_description_"],
input[id*="style_pledgie_id"],
input[id*="search-terms"],
input[id*="user_login"],
input[id*="password"],
input[id*="password_confirmation"],
input[id*="user_name"],
input[id*="user_email"],
input[id*="user_homepage"],
input[type*="url"],
input[id*="user_paypal_email"],
input[id*="widget-title"],
input[id*="obsoleting-style-id"] {
-webkit-appearance: textfield;
padding: 2px;
background-color: #333333 ! important;
border: 1px solid #202020 ! important;
border-top: 1px solid #2b2b2b ! important;
border-image-source: initial;
border-image-slice: initial;
border-image-width: initial;
border-image-outset: initial;
border-image-repeat: initial;
-webkit-rtl-ordering: logical;
-webkit-user-select: text;
cursor: auto;
color: #dddddd ! important;
}
input[id*="setting-"] {
border: 1px solid #202020 ! important;
border-top: 1px solid #2b2b2b ! important;
}
select {
-webkit-appearance: menulist;
box-sizing: border-box;
align-items: center;
border: 1px solid #202020 ! important;
border-image-source: initial;
border-image-slice: initial;
border-image-width: initial;
border-image-outset: initial;
border-image-repeat: initial;
white-space: pre;
-webkit-rtl-ordering: initial;
color: #dddddd ! important;
background-color: #333333 ! important;
cursor: default;
}
.notice {
background-color: #e1e1e1 ! important;
border: 1px dashed #c7c7c7 ! important;
text-align: center ! important;
}
@media(max-width:900px) {
#left-sidebar {
float:none ! important;
width:auto ! important;
margin-left:5px ! important;
margin-top:1em ! important;
}
#content-wrapper {
float:none ! important;
width:auto ! important;
}
#main-article {
margin-left:5px ! important;
margin-right:5px ! important;
}
#search-terms,#search-submit,#show-advanced-search {
float:none ! important;
}
#search-terms {
width:85% ! important;
}
#show-advanced-search {
text-align:left ! important;
}
nav form {
margin-bottom:.5em ! important;
}

}
@media(max-width:600px) {
.listing-left-info {
float:none ! important;
width:auto ! important;
}
.screenshot-thumbnail {
text-align:left ! important;
}
.style-brief-text {
margin-left:0 ! important;
}
.style-brief,#browse-middle-ad {
padding-top:15px ! important;
border-bottom:2px solid gray ! important;
padding-bottom:2em ! important;
}
.style-brief:last-of-type {
padding-bottom:5px ! important;
}
.style-brief-stats span {
display:inline ! important;
}
.style-brief-stats span::after {
content:"|" ! important;
}
.style-brief-stats span:last-child::after {
content:"" ! important;
}
#style-author-info {
margin-left:0 ! important;
}
#left-info {
float:none ! important;
width:auto ! important;
}
#main-style-info {
margin-left:0 ! important;
}
#info-tables th {
min-width:150px ! important;
}
#social {
height:auto ! important;
}
h1 {
font-size:1.25em ! important;
}
}




#Head h1 a span {
font-size: 24px;
color: #2793e6 ! important;
}
#Head {
background: #1e1e1e ! important;
padding: 0;
border: 1px solid #1b1b1b ! important;
}
#Head form input.InputBox {
width: 204px;
border: 0;
padding: 4px;
margin: 0 6px 0 0;
background: #333333 ! important;
border: 1px solid #202020 ! important;
border-top: 1px solid #2b2b2b ! important;
-moz-border-radius: 2px;
-webkit-border: 2px;
border-bottom: 2px;
color: #dddddd ! important;
font-size: 13px;
vertical-align: bottom;
}
.Box {
border-radius: 1px;
-moz-border-radius: 1px;
-webkit-border-radius: 1px;
background: #202020 ! important;
border: 1px solid #1d1d1d ! important;
color: #888;
padding: 6px 10px;
margin: 0 0 10px;
box-shadow: 0px 0px 2px #999;
-moz-box-shadow: 0px 0px 2px #999;
-webkit-box-shadow: 0px 0px 2px #999;
}
.PanelInfo {
border-top: 1px solid #3b3b3b ! important;
border-bottom: 0;
}
.PanelInfo .Active {
background: #3b3b3b ! important;
border-bottom: 1px solid #3b3b3b ! important;
}
.PanelInfo li {
background: #282828 ! important;
border-bottom: 1px solid #3b3b3b ! important;
padding: 2px 4px;
text-align: right;
}
.Count {
background: #212121 ! important;
background-color: rgba(0, 0, 0, .6);
border-radius: 3px;
-webkit-border-radius: 3px;
-moz-border-radius: 3px;
border-radius: 3px;
color: #909090 ! important;
font-size: 80%;
font-weight: normal;
padding: 1px .8ex;
}
#Content {
float: left;
width: 680px;
margin: 0 0 40px;
background: #191919 ! important;
}
.DataList .Item {
margin: 0;
padding: 4px 0;
border: 0;
position: relative;
border-bottom: 1px solid #2b2b2b ! important;
}
.DataList .Meta .Announcement {
background: #222222 ! important;
color: #A0A0A0 ! important;
border: 1px solid #3b3b3b ! important;
padding: 1px 4px;
border-radius: 2px;
-moz-border-radius: 2px;
-webkit-border-radius: 2px;
border-radius: 2px;
font-weight: bold;
}
.DataList .Meta strong {
border-radius: 2px;
-moz-border-radius: 2px;
-webkit-border-radius: 2px;
background: #212121 ! important;
color: #909090 ! important;
border: 1px solid #2b2b2b ! important;
font-size: 9px;
font-weight: bold;
padding: 3px;
line-height: 1;
}
.DataList .Meta a.Category {
background: #222222 ! important;
color: #A0A0A0 ! important;
border: 1px solid #3b3b3b ! important;
padding: 2px 4px;
border-radius: 2px;
-moz-border-radius: 2px;
-webkit-border-radius: 2px;
}
.DataList .Meta a.Category:hover {
color: #2793e6 ! important;
}
.Item .OptionsMenu {
vertical-align: top;
visibility: visible ! important;
}
.MessageForm, .Tabs, .Headings {
display: block;
border-bottom: 1px solid #2b2b2b ! important;
padding: 5px 8px;
margin: 0;
background: #191919 ! important;
position: relative;
}
.Active .TabLink {
background: #2b2b2b ! important;
color: #474747;
}
.TabLink, .TabLink:link, .TabLink:hover, .TabLink:active, .TabLink:visited {
margin: 0;
border: 1px solid #3c3c3c ! important;
border-radius: 3px;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
background: #191919 ! important;
display: inline;
font-weight: bold;
color: #1e79a7;
text-decoration: none;
padding: 5px 10px;
line-height: 2.6;
white-space: nowrap;
}
.Tabs li a span {
line-height: 1;
font-size: 80%;
padding: 0 3px;
border-radius: 3px;
-webkit-border-radius: 3px;
-moz-border-radius: 3px;
font-weight: normal;
background: #333333 ! important;
color: #909090 ! important;
margin: 0 0 0 4px;
}
.User h1 {
padding: 0 0 8px;
margin: 0;
line-height: 100%;
border: 0;
color: #909090 ! important;
}
.Box h4 {
font-size: 110%;
color: #909090 ! important;
font-weight: bold;
margin: 0;
padding: 0 0 1px;
}
body.Profile .PanelInfo {
border-bottom: 1px solid #3b3b3b ! important;
}
body ul div.Comment blockquote.UserQuote {
padding: 5px 10px;
margin-right: 10px;
font-style: italic;
background-color: #202020 ! important;
border: 1px dashed #3b3b3b ! important;
}
div.Comment blockquote.UserQuote div.QuoteText {
color: #909090 ! important;
border-left: 2px solid #3b3b3b ! important;
padding-left: 5px;
}
li[class="Item Comment Mine"],
li[class="Item Comment Mine Alt"] {
background-color: #222222 ! important;
}
.Attachment:hover .FileHover {
z-index: 101;
display: block;
background: #202020 ! important;
padding: 6px;
border: 1px solid #3b3b3b ! important;
box-shadow: 0px 2px 10px #000;
-moz-box-shadow: 0px 2px 10px #000;
-webkit-box-shadow: 0px 2px 10px #000;
position: absolute;
top: -7px;
left: -7px;
}
div.Popup .Body {
background: #202020 ! important;
}
div.Popup h1, div.Popup h2 {
font-size: 120%;
font-weight: bold;
margin-bottom: 0;
color: #909090 ! important;
background: #1e1e1e ! important;
border-bottom: 1px solid #1b1b1b ! important;
padding: 6px 9px 4px;
}
.Warning {
background: #1e1e1e ! important;
border: 1px dashed #3b3b3b ! important;
color: #909090 ! important;
padding: 6px 8px;
border-radius: 3px;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
}
div.Popup form ul li label {
color: #909090 ! important;
display: block;
font-size: 14px;
font-weight: bold;
margin: 10px 0 0;
}
code {
border-radius: 2px;
-moz-border-radius: 2px;
-webkit-border-radius: 2px;
background: #202020 ! important;
padding: 4px 8px;
white-space: pre;
font-family: monospace;
overflow: auto;
border: 0px dashed #3b3b3b ! important;
}
.Activities li.Condensed .Excerpt {
color: #909090 ! important;
font-size: 13px;
padding: 0 0 0 4px;
}
body.Profile ul.Activities {
margin-top: 10px;
border-top: 1px solid #3b3b3b ! important;
}
#MessageForm {
background: #191919 ! important;
border-bottom: 1px solid #2b2b2b ! important;
padding: 4px 8px 8px;
margin: 1px 0 0;
border-bottom-left-radius: 2px;
border-bottom-right-radius: 2px;
-moz-border-radius-bottomleft: 2px;
-moz-border-radius-bottomright: 2px;
-webkit-border-bottom-left-radius: 2px;
-webkit-border-bottom-right-radius: 2px;
}
#MessageForm h2 {
font-size: 115%;
color: #909090 ! important;
font-weight: bold;
border: 0;
}
.MessageList .Item {
border-bottom: 1px solid #3b3b3b ! important;
padding: 10px 10px 4px 10px;
}
h1, h2, h3, h4, h5, h6 {
color: #909090 ! important;
font-family: "Helvetica Neue",Helvetica,arial,sans-serif;
font-weight: normal;
margin: 0;
}
#ConversationForm form {
border-radius: 2px;
-moz-border-radius: 2px;
-webkit-border-radius: 2px;
border-radius: 2px;
background: none repeat scroll 0 0 #1e1e1e ! important;
margin: 0;
padding: 20px;
text-align: right;
}
section#obsoletion-message {
background-color: #2e2e2e ! important;
border-radius: 3px;
margin-bottom: 2em;
padding-right: 15px;
}
span.Title {
color: #909090 ! important;
font-size: 14px;
font-weight: bold;
display: inline !important;
}
.lb-outerContainer {
position: relative;
background-color: #1e1e1e ! important;
width: 250px;
height: 250px;
margin: 0 auto;
-webkit-border-top-left-radius: 4px ! important;
-webkit-border-top-right-radius: 4px ! important;
-webkit-border-bottom-left-radius: 0px ! important;
-webkit-border-bottom-right-radius: 0px ! important;
-moz-border-radius: 4px;
-ms-border-radius: 4px;
-o-border-radius: 4px;
border-radius: 4px;
border-top: 1px solid #2b2b2b ! important;
border-left: 1px solid #2b2b2b ! important;
border-right: 1px solid #2b2b2b ! important;
}
.lb-dataContainer {
margin: 0 auto;
padding-top: 5px;
width: 100%;
-moz-border-radius-bottomleft: 4px;
-webkit-border-bottom-left-radius: 4px;
border-bottom-left-radius: 4px;
-moz-border-radius-bottomright: 4px;
-webkit-border-bottom-right-radius: 4px;
border-bottom-right-radius: 4px;
background-color: #1e1e1e ! important;
border-left: 1px solid #2b2b2b ! important;
border-right: 1px solid #2b2b2b ! important;
border-bottom: 1px solid #2b2b2b ! important;
}
.errorExplanation {
padding: .5em;
background-color: #414141 ! important;
margin-top: .5em;
margin-bottom: .5em;
border: 1px solid #8f8f8f ! important;
}

div[id="post-install-ad"] {
display: none ! important;
}
div[style="width: 300px; height: 262px;"] {
display: none ! important;
}


}
