css:
@-moz-document domain("imo.im") {
body { color: #ccc }
message.unread { color: #7cbc00 }
photos button.action { color: #fff }

#main, photos .content, search .content { background-color: #222 !important}
broadcast > *, .broadcast-menu ul li, .info-left, controls, contact, .window-actions, textarea { background-color:#333 !important }
.right-info > *,contacts h1, header, .card, profile icon { background-color: #2d2d2d !important }
message, .typing, windows > window, settings .content, profile .content, group-members > div { background-color: #3f3f3f !important }
photos button.action, #main > header ul.nav > li.selected button { background-color: #7cbc00 }

#createBroadcast > div.compose-area { background: none }
:focus { background: #3f3f3f !important }

.right-info:before, contact > .wrapper:before, .joiner { display:none }

.action_menu { padding-bottom: 22px }

#createBroadcast .icon-container { border: solid 10px #333 !important }
header, contact { border-bottom: 1px solid #222 }
broadcast controls, window form { border-top: 1px solid #222}
contact, contacts h1 { border-left: 1px solid #222 !important }
}
