css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jd.com") {
    body{background-color: #151515 !important;}
    *{color: #C7EDCC ; }
    /*img{filter: alpha(opacity=70);}*/
    img{opacity:0.7;}
    
    span{color: #C7EDCC !important;}
    a{color: #C7EDFF !important; background-color: transparent !important;}
    a[class*="btn"]{ background: none !important;background-color: #555555 !important;}
    a.research{background: #555555 !important;}
    a.gotop{background: #555555 !important;}
    a[href*="adclick"]{display: none;}
    
    div.mc{color: #C7EDFF !important; background-color: #151515 !important;}
    div.i-mc{color: #C7EDFF !important; background-color: #151515 !important;}
    div[id^="shortcut"]{ background-color: #151515 !important;}
    div[id^="service"]{ background-color: #151515 !important;}
    div.w{ background-color: #151515 !important;}
    div.dd{ background-color: #151515 !important;}
    div.mt{ background-color: #151515 !important;}
    div.text{ background-color: #151515 !important;}
    div.content{ background-color: #151515 !important;}
    div[class*="item"]{ background:none !important; background-color: #151515 !important;}
    div.outer{ background-color: #151515 !important;}
    div[class*="publish"]{ background-color: #151515 !important;}
    div.aside{ background-color: #b2b2b2 !important;}
    div.lh-wrap{ background-color: #151515 !important;}
    div[class*="fore"]{ background-color: #151515 !important;}
    div#settleup-content{ background-color: #151515 !important;}
    div[class*="sm"]{ background-color: #151515 !important;}
    div.viewlist{ background-color: #151515 !important;}
    div.mb{ background-color: #151515 !important;}
    div.mt{ background: #151515 !important;}
    div.cart-total.clearfix{ background: #151515 !important;}
    div.item.item_selected{ background: #151515 !important;}
    div.cart-thead.clearfix{background: #888888 !important;}
    div.column{color: #C7EDCC !important;}
    div[class*="dialog"]{ background: #151515 !important;}
    div#inventory{background-color: #555555 !important;}
    
    input{ color: #C7EDCC !important;background-color: #555555 !important;}
    input[type="button"]{ background: #555555 !important;}
    
    h3{color: #C7EDCC !important; background-color: transparent !important;}
    
    dt{color: #C7EDCC !important; background-color: #151515 !important;}
    dt.ld{background-color: #151515 !important;}
    dd{background-color: #151515 !important;}
    
    td{background-color: #151515 !important;}
    th{background-color: #151515 !important;}
    
    span.outline{background-color: #151515 !important;}
    span.blank{background-color: #151515 !important;}
    span[class*="disabled"]{background-color: #555555 !important;}
    
    select{background-color: #151515 !important;}
    
    ul{ background-color: #151515 !important;}
    
    li{ background-color: transparent;}
    li.dt{background-color: #888855 !important;}
    .tab li{background: #555555 !important; position: static !important;}
}
