@-moz-document domain('twitter.com') {
html,body{font-family:Georgia,"Hiragino Sans GB",Arial,"Songti SC",SimSun,sans-serif !important;}.promptbird,.nav>li>a .text,.topbar-tweet-btn,.BannersContainer,.wrapper-home .dashboard,.wrapper .mini-profile,.wrapper .wtf-module,.wrapper .trends,.wrapper .Footer,.wrapper .enhanced-media-thumbnails,.wrapper .module.component,.wrapper .newest-members-module,.header-inner,.is-preview,.expanded-content,.tweet-actions,.signup-call-out.profile-signup-call-out{display:none !important;}.global-nav .container{width:730px !important;min-width:730px !important;max-width:730px !important;}.global-nav .form-search{margin-right:0 !important;}.nav{margin-left:60px !important;}.global-nav .pull-right .right-actions{margin-left:0 !important;}.nav.right-actions>li>a,.nav.right-actions>li>button{margin-right:0 !important;margin-left:15px !important;}.session .dropdown-menu,.global-nav .pull-right .dropdown-menu,.global-nav .secondary-nav .dropdown-menu{margin:15px -22px 0 0 !important;}.bird-topbar-etched,.pushstate-spinner{margin:0 !important;position:absolute !important;left:10px !important;top:15px !important;}.wrapper,.wrapper-narrow{width:auto !important;min-width:730px !important;max-width:730px !important;padding:60px 0 30px !important;}.profile-card.profile-header{width:100% !important;padding:0 !important;float:none !important;}.wrapper .dashboard{float:none !important;position:absolute !important;left:-170px !important;top:60px !important;width:160px !important;}.content-main{float:none !important;width:100% !important;}.new-tweets-bar{padding:15px 0 !important;font-size:14px !important;}.stream-item-activity-me .latest-tweet{font-size:15px !important;}.stream-item-header .avatar,.permalink-header .avatar{margin-top:2px !important;}.stream-item.open .is-preview,.stream-item.open .expanded-content{display:block !important;}li.stream-item,.tweets-wrapper li,.recent-tweets li{font-size:16px !important;line-height:26px !important;}.tweet{padding-top:12px !important;}.tweet .discover-social-context .Icon,.tweet .discover-summary-context .Icon{margin-top:5px !important;}.stream-item-activity{padding-top:6px !important;}.stream-item-activity-me .activity-type{top:14px !important;}.tweet .stream-item-footer{padding-top:0 !important;}.stream .stream-item .tweet-actions,.stream .stream-item.open .tweet-actions,.stream .stream-item-activity .stream-item-footer .tweet-actions,.stream .stream-item.open .expanded-conversation .original-tweet-container .tweet-actions{height:30px !important;background:none !important;box-shadow:0 0 0 0 rgba(255,255,255,0) !important;}.stream-item-activity-me .latest-tweet .tweet-row{max-width:100% !important;}.tweet:hover .tweet-actions,.stream-item.open .tweet-actions{display:inline-block !important;}.wrapper .profile-card.module.component{display:block !important;}.profile-header-inner-overlay{width:100% !important;}.wrapper-list h3{font-size:16px !important;}
}
