css:
@-moz-document domain('j-archive.com') {

body {
background: #001F3F;
}
#navbar{
position: fixed;
background: black;
z-index: 1000;
width: 100%;
top: 0;
height: 50px;
}
#navbarlogo {
height: 50px;
line-height: 60px;
}
#navbartext {
height: 50px;
line-height: 50px;
background: white;
}
#navbartext a {
padding: 0 20px;
margin: 0;
height: 50px;
display: inline-block;
text-transform: uppercase;
font-size: 125%;
transition: all 0.5s ease;
}
#navbartext a:hover {
background: yellow;
color: black;
}
#navbarsearch {
margin: 0;
height: 50px;
line-height: 50px;
}
input.search {
border: 0;
background: yellow;
height: 46px;
width: 200px;
}
input.search_button {
height: 50px;
border: 0;
background: red;
}
#content {
top: 50px;
left: 0;
right: 0;
margin: auto;
}
.contestants {
text-align: left;
}
.contestants a {
font-size: 175%;
}
}
