css:
@-moz-document domain("volafile.io") { 
    /*Don't change uploading status */
    html.room #file_list .file.uploading .fstatus { display: inline-block!important }
    /*Colour Things*/
    mark { background: rgb(21,21,21) !important;
           color: rgb(114,100,172)!important;
    }
    textarea, input[type=text], input[type=password] { background: rgb(28,25,31) !important;
                                                       color: rgb(119,119,119)!important;
    }
    .button, input[type=submit] { background: rgb(160,120,190) !important;
                                  color: rgb(21,21,21)!important;
    }
    .button:hover, input[type=submit]:hover { background: rgb(120,100,172) !important }
    .button:active, input[type=submit]:active { background: #e8b0ff !important }
    .toggle i { color: rgb(89,89,89)!important }
    .nano>.pane>.slider { background: rgba(49,59,49,0.8) !important }
    .nano>.pane>.slider:hover { background: rgb(59,69,59) !important }
    Html.room #file_list .file.failed { background: red !important }
    .file_tag.tag_key_user { background: rgb(44,44,44) !important;
                             border-color: rgb(99,99,99)!important;
                             color: rgb(139,139,139)!important;
    }
    .file_tag { background: rgb(31,31,31) !important;
                border-color: rgb(99,99,99)!important;
                color: rgb(139,139,139)!important;
    }
    .file_tag:hover { background: rgb(20,20,20) !important;
                      color: rgb(126,91,172)!important;
    }
    html.room #overlay { background: rgb(21,21,21) !important;
                         color: rgb(89,89,89)!important;
    }
    .highlight b { color:rgb(12,10,15)!important }
    * { border-color: rgb(60,50,50)!important }
    * { border-radius: 0!important }
    #room_name_container { color: rgb(160,120,190)!important;
                           text-align: center!important;
    }
    #chat_messages .message.admin { background: rgb(12,10,15) !important }
    .fcontrol, .fsize, .fstatus, .toggle i, .fcontrol, I.fa.fa-download.clickable { color: rgb(145,132,125)!important }
    .fcontrol i.clickable { color: rgb(110,135,120)!important }
    html.room #file_list .file.uploading, html.room #file_list.uneven .file.uploading:nth-child(2n+1), html.room #file_list.even .file.uploading:nth-child(2n) { background: rgb(65,60,70) !important }
    
    html.room #chat_frame #chat_messages_frame #chat_messages .message.admin b {color:rgb(12,10,15)!important}
    
    input[type=text].defaultValue, input[type=password].defaultValue, body, html.room #header #room_search #clearsearch, a, a:visited { color: rgb(135,140,135)!important }
    textarea.defaultValue{ color: rgb(12,10,15)!important}
    
    .toggle.enabled i, html.index h1.logo, html.index h1.logo span, html.room #header #header_row_container a:hover i.green-icon, a:hover, html.room #header #room_search #clearsearch:hover, html.room #file_list .file .fcontrol i.clickable:hover, a:active, html.room #header #room_search #clearsearch:active, html.room #file_list .file .fcontrol i.clickable:active, html.room #file_list .file.playing i.filetype, html.room #file_list .file.loading i.filetype, .file a.fname:hover, html.room #file_list .file.uploading .fstatus { color: rgb(140,120,150)!important }
    html, html.room #header, div.ui_frame { background: rgb(12,10,15) !important }
    html.room #header #header_row_container a i.green-icon, html.room #header #header_row_container a:active i.green-icon { color: rgb(110,180,120)!important }
    html.room #chat_frame #chat_messages_frame #chat_messages .message.highlight, html.room #chat_frame #chat_hbar { background: rgb(12,10,15) !important }
    html.room #chat_frame #chat_messages_frame #chat_messages .message.user b { color: rgb(12,10,15)!important }
    html.room #file_list.uneven .file:nth-child(2n+1) { background: rgb(28,25,31)!important }
    html.room #file_list.even .file:nth-child(2n) { background: rgb(12,10,15)!important }
    html.room #file_list .file:nth-child(2n) { background: rgb(12,10,15)!important }
    html.room #file_list.uneven .file:nth-child(2n) { background: rgb(12,10,15)!important }
    html.room #file_list.even .file:nth-child(2n+1) { background: rgb(28,25,31)!important }
    html.room #chat_frame #chat_messages_frame #chat_messages .message.user { background: rgb(12,10,15)} 
    #chat_messages .message b {color: rgb(12,10,15)!important} 
}
