blockquote,pre,abbr,acronym,address,big,cite,code,del,dfn,em,font,img,ins,kbd,q,s,samp,small,strike,strong,sub,sup,tt,var,b,u,i,center,dl,dt,dd,fieldset,form,label,legend,table,caption,tbody,tfoot,thead,tr,th,td,h1,h2,h3,h4,h5,h6 {-webkit-box-sizing: padding-box;}
span,object,iframe,ol,ul,table,div{-webkit-box-sizing:padding-box;}
body {
background-color: rgba(0,98,20,1)!important;
background:-webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0, 0, 0, 0.5)), color-stop(50%,rgba(0,198,120, 0.75)), color-stop(100%,rgba(0, 0, 0, 0.5)));
background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0, 0, 0, 0.5)), color-stop(50%,rgba(0,198,120, 0.75)), color-stop(100%,rgba(0, 0, 0, 0.5)));
background-attachment: fixed;
font:normal 13px/20px helvetica,arial,sans-serif;
}
h1,h2,h3,h4,h5,h6 {
background-color: rgba(0,198,120,0.5)!important;
background:-webkit-gradient(linear, left top, right top, color-stop(0%,rgba(0, 0, 0, 0.75)), color-stop(50%,rgba(0, 198, 120, 0.5)), color-stop(100%,rgba(0, 0, 0, 0.75)));
background-image: -webkit-gradient(linear, left top, right top, color-stop(0%,rgba(0, 0, 0, 0.75)), color-stop(50%,rgba(0, 198, 120, 0.5)), color-stop(100%,rgba(0, 0, 0, 0.75)))!important;
background-clip: padding-box;
background-origin: content-box;
color:#dfffaf!important;
text-shadow: 0 1px 0 rgba(0, 0, 0, 0.5), 0 2px 1px rgba(0, 0, 0, 0.4), 0 3px 2px rgba(0, 0, 0, 0.3), 0 4px 3px rgba(0, 0, 0, 0.2);
}

p {
background-color: rgba(0,0,0,0.8)!important;
background-image:-webkit-gradient(linear, left top, right bottom, color-stop(0%,rgba(0,98,20,0.35)), color-stop(50%,rgba(0,98,20, 0.5)), color-stop(100%,rgba(0,98,20,0.25)));
color:#dfffcf!important;
text-shadow: 0 1px 1px rgba(0, 0, 0, 0.7);
background-clip: padding-box;
background-origin: content-box;
}
strong {
background-color: rgba(0,98,20,0.5)!important;
background:-webkit-gradient(linear, left top, right top, color-stop(0%,rgba(0, 0, 0, 0.5)), color-stop(50%,rgba(0,198,120, 0.35)), color-stop(100%,rgba(0, 0, 0, 0.5)));
background-image: -webkit-gradient(linear, left top, right top, color-stop(0%,rgba(0, 0, 0, 0.5)), color-stop(50%,rgba(0,198,120, 0.75)), color-stop(100%,rgba(0, 0, 0, 0.5)));
font-weight: 600;
color: rgb(227, 247, 191)!important;
}
pre,abbr,acronym,address,big,cite,code,del,dfn,em,font,b,u,q,s,samp,small,strike,sub,sup,tt,var {
background-color: rgba(0,98,20,0.5)!important;
background-image:-webkit-gradient(linear, left top, right bottom, color-stop(0%,rgba(0, 98, 20, 0.5)), color-stop(50%,rgba(0, 98, 20, 0.75)), color-stop(100%,rgba(0, 98, 20, 0.50)))!important;
color: rgb(182, 255, 234)!important;
}
a {
background-color: rgba(0,0,0,0)!important;
color: rgb(212, 230, 204)!important;
text-transform: capitalize;
}
a:hover {
background-color: rgba(0,248,200,0.5)!important;
background:-webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(1, 198, 120, 0.25)), color-stop(50%,rgba(0, 198, 120, 0.75)), color-stop(100%,rgba(1, 198, 120, 0.5)));
color: rgba(225, 255, 255, 1)!important;
}
a img {
background-color: rgba(0, 98, 20, 1)!important;
background: -webkit-linear-gradient(right top,rgba(0, 0, 0, 1) 0%, rgba(0, 98, 20, 0.5) 25%, rgba(0, 198, 120, 0.5) 50%,rgba(0, 98, 20, 0.5) 75%,rgba(0, 0, 0, 1) 100%);
padding:2px 3px;
box-shadow:rgba(0, 198, 120, 0.5) 0 0 1px inset, 5px 10px 15px rgba(0, 0, 0, 0.5);
}
