css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www.google.com/calendar/), url-prefix(https://www.google.com/calendar/), url-prefix(javascript:parent.gtasks.embed.createHtmlForTasksFrame) {

/* Google: User Menu with Profile settings */
#gbw #gbu.gbes .gbtc.gbsr {
  border:1px solid #555 !important;
  border-left:3px solid #cc4235 !important;
  position:fixed !important;
  z-index:100 !important;
  right:-337px !important;
  top:0 !important;
  width:333px !important;
  background:#f0f0f0 !important;
  padding:10px 10px 10px 20px !important;
  box-shadow: -3px 4px 10px #777 !important;
  transition-duration: 1s;
  transition-timing-function: linear;
  transition-delay: .8s;
}
#gbw #gbu.gbes .gbtc.gbsr:hover {
  border:1px solid #555 !important;
  right:-1px !important;
  transition-duration: .2s;
  transition-timing-function: linear;
  transition-delay: .15s;
}

/* User Pic */
.gbmpiaa.gbp1 {
  width:120px !important;
  height:120px !important;
  background: url(https://lh6.googleusercontent.com/-1fpYqfrNzNo/AAAAAAAAAAI/AAAAAAAAB38/TWUhqCM3YJ8/s120-c/photo.jpg) no-repeat;
  margin-bottom:10px !important;
}
/* скрыть старое фото и лишнюю подпись о смене фото */
.gbxv, img#gbmpi {display:none !important;}
/* опция сменить фотографию */
#gbmpicp, #gbmpicb {
  width:120px !important;
  bottom:10px !important;
  font:11px tahoma !important;
  height:13px !important;
}

/* === */
/* Календарь: патч ширины меню c шестерёнкой */
.goog-menu {width:325px !important;}

/* Hide the control bar that allows you to hide/show tasks frame */
#rhstogglecell { display: none !important;}

/* Resize main calendar cell, by enlarging the right margin */
#maincell {
}
/* cal event list */
#maincell.has-sn-ex #mainbody.printAlignLeft {
  margin-right: -160px !important;
  margin-left:30px !important;
}

/* edit cal_event */
#maincell.has-sn-ex #chrome_cover2 #coverinner .ep {

}

/* TODO List: Enlarge task frame */
#gadgetcell {
  width:27px !important;
  height:97% !important;
  border-left:3px solid #cc4235 !important;
  border-top:1px solid #555 !important;
  border-bottom:1px solid #555 !important;
  margin-right:0 !important;
  box-shadow:-3px 4px 10px #777 !important;
  z-index:10 !important;
  margin-top:10px !important; 
  transition-duration: 1s;
  transition-timing-function: linear;
  transition-delay: .8s;
}
#gadgetcell:hover {
  width:363px !important;
  margin-right:0 !important;
  z-index:10 !important;
  border-left:1px solid #555 !important;
  transition-duration: .2s;
  transition-timing-function: linear;
  transition-delay: .15s;
}
/* Enlarge task frame contents */
#gadgetcell .sn-frame {width: 363px !important;}
#gadgetcell.gadgetcell-closed {width: 0px !important;}
#gadgetcell.gadgetcell-closed .sn-frame {width: 0px !important;}

.z {margin-right:6px !important;}
.Ab {background:#e8e8e8 !important;}
.ub.goog-inline-block {
  position:relative !important;
  top:-2px !important;
  padding:0 7px 0 10px !important;
}
.a.eb.pc.goog-inline-block {
  position:relative !important;
  top:-1px !important;
}

/* Resize the Notes field in the Task Editor */ 
textarea[class="j ec L"] {min-height: 400px !important;}
textarea[class="j ec"] {min-height: 400px !important;}

.sng-title-bar {display:none !important;}
.Sb {
  padding:2px !important;
  border-bottom:1px solid #000 !important;
  background:#f0f0f0 !important;
}
.sn-wrapper {height:100% !important;}
.sn-frame, .sn-solo, .sng-frame, .sng-wrapper, .sng-content {height: 100% !important;}
.sng-content {height: 100% !important;}
.p .b {
  font:11px tahoma !important;
  padding:0 !important;
  margin:0 !important;
}

#calcontent * {font-family:Tahoma !important;}

/* границы контейнера для задач */
dl.cbrd {
  position:relative !important;
  z-index:1 !important;
  border-left:0 !important;
  border-right:0 !important;
}

/* значки погоды */
.wk-webcontent-td .st-wc {
  top:3px !important;
  margin-left:3px !important;
  position:absolute !important;
  border:1px solid #ccc !important;
  z-index:530 !important;
}

/* заголовок дня недели */
.wk-dayname {
  background:#f0f0f0;
  color:#000;
}

/* размер шрифта в подменю из туду */
.goog-menuitem-content {font:11px tahoma !important;}

/* время у задачи */
dl.cbrd dt{
  font-size:7px !important;
  position:relative !important;
  margin-left:-5px !important;
  z-index:1 !important;
  top:-15px !important;
  line-height:32px !important; /* для строк больше чем 1 */
  overflow-x:hidden !important;
  overflow-y:hidden !important;
  right:0;
}

/* текст однострочных задач */
dl.cbrd dt span.cbrdcc {
  font-size:11px !important;
  position:relative !important;
  top:11px !important; /* высота строки с текстом*/
  margin-left:-24px !important;
  overflow-x:scroll !important;
  overflow-y:hidden !important;
}

/* если больше одной строки */
dl.cbrd dd {
  margin-top:-28px !important;
  margin-left:-3px !important;
  font-size:11px !important;
}

/* нижний край ресайзера на событии календаря */
.resizer {
z-index:6 !important;
}
.rszr-icon {margin-top:4px !important;}
.rszr-icon-hover {margin-top:4px !important;}
.chip-border {margin:-2px !important;}
.cbrd {padding:3px !important;}
.mask-left, .mask-right {height:100% !important;}
.cic, .cic-tmr {display:none !important;}


/* ======================= */
/* Calendar: LEFT MAIN MNU */
#mothertable > #nav {
  margin-left:-0px !important;
  margin-top:-55px !important;
  padding:9px 30px 20px 30px !important;
  left:-180px !important;
  border-right:3px solid #cf4536 !important;
  border-top:1px solid #555 !important;
  border-bottom:1px solid #555 !important;
  z-index:10 !important;
  background:#fff !important;
  box-shadow: 3px 4px 10px #777 !important;

  transition-duration: 1s !important;
  transition-timing-function: linear !important;
  transition-delay: .8s !important;
}
#mothertable > #nav:hover {
  left:0 !important;
  border-right:1px solid #555 !important;

  transition-duration: .2s !important;
  transition-timing-function: linear !important;
  transition-delay: .15s !important;
}

/* Навигация по дням неделям месяцам + настройки и т.п. */
#topRightNavigation {
  position:relative !important;
  left:500px !important;
  width:500px !important;
}

/* Внутренняя тень в календаре */
.wk-weektop.wk-full-mode {
  box-shadow: 1px  3px 5px #777 !important;
  z-index:5 !important;
  position:relative !important;
}

.goog-menu.goog-menu-vertical {width:280px !important;}

/* задачи на нексолько дней */
.evt-lk {font:11px tahoma !important;}

/* "Сегодня" и кнопки навигации */
.nav-table, .topLeftNavigation {
  position: absolute !important;
  left:50px !important;
}

/* шкала времени слева */
.tg-times-pri div {text-align:center !important;}

/* ячейки в теблице с задачами */
.tg-markercell {}
.tg-col .tg-dualmarker {}
.tg-col .tg-dualmarker:hover {background:#f0f !important;}

/* редкатирвоание задачи */
.ep {
  margin-top:50px !important;
  margin-left:144px !important;
  position:relative !important;
}

/* уведомление календаря */
.cal-dialog {
min-width:680px !important;
width:680px !important;
max-height:100% !important;
min-height:auto !important;
height:auto !important;
top:25% !important;
}
.cal-dialog-content {
position:relative !important;
margin:0 !important;
padding:10px !important;
min-width:650px !important;
width:650px !important;
max-height:100% !important;
min-height:auto !important;
height:auto !important;
}
.erd-minwidth {
min-width:650px !important;
width:650px !important;
max-height:100% !important;
min-height:auto !important;
height:auto !important;
}
.erd-container {
box-shadow:inset 0px 0px 4px #777 !important;
border:1px solid #aaa;
min-width:650px !important;
width:650px !important;
position:relative !important;
margin:0 !important;
padding:3px !important;
overflow-x:hidden !important;
font:11px tahoma !important;
max-height:100% !important;
min-height:auto !important;
height:auto !important;
}
/* строки напоминания о мероприятиях */
.erd-event {
width:auto !important;
height:10px;
margin:0px 0px 0px 0px !important;
padding:5px 10px 10px 10px !important;
}
.erd-event:nth-child(2n) { /* :nth-child(2n+1)*/
background:#eee;
padding:4px 10px 10px 10px !important;
}
.erd-event-first {
width:auto !important;
height:9px;
margin:0px 0px 0px 0px !important;
padding:5px 10px 10px 10px !important;
}
/* кнопка OK напоминания о мероприятиях */
BUTTON.goog-buttonset-default {
margin:4px -4px 10px 0px !important;
padding:3px 50px 3px 50px !important;
float:right !important;
cursor:pointer !important;
}

/* столбцы в календаре с задачами */
.tg-col {
border:0 !important;
z-index:20 !important;
}
.tg-col-today {
border:0 !important;
}
/* слой поверх столбцов в календаре с задачами */
.tg-col-eventwrapper{
border-right:1px solid #ccc !important;
}

.tg-col-eventwrapper:nth-child(2n) {
border-right:1px solid #ccc !important;
box-shadow:inset 0px 0px 4px #000 !important;
background:none !important;
z-index:9 !important;
}

/* четные строки в календаре с задачами */
.tg-markercell:nth-child(2n) {
background:#f7faf7 !important;
z-index:9 !important;
}
/* четные строки в TODO */
.v .p:nth-child(2n) {
background:#f4f4f4 !important;
}
/* фон временных рамок через строку */
.tg-times-pri div:nth-child(2n) {
background:#f3f3f3 !important;
}
.tg-times-pri {
border-right:1px solid #777 !important;
}
/* указатель текущего местоположения на календаре задач */
DIV#tgnowmarker.tg-hourmarker.tg-nowmarker {
height:28px !important;
opacity:0.9 !important;

border-top:1px dotted #000 !important;
border-bottom:1px dotted #000 !important;
border-left:3px solid #f00 !important;
border-right:3px solid #f00 !important;

margin:-17px 0 0 -6px !important;
padding:0 !important;
width:101% !important;
}
/* маркер текущего времени слева */
DIV#tgnowptr.tg-nowptr { /* https://calendar.google.com/googlecalendar/images/combined_v43_vr.png */
opacity:0.15 !important;
margin:0px !important;
height:0px !important;
width:100% !important;
z-index:8 !important;
background:none !important;
border-bottom:3px solid red;
border-left:6px solid #fff !important;
border-top:3px solid red;
}
/* текущая задача в календаре и её свдиг вправо */
.chip {
margin-left:4px !important;
}
} /* END of @-moz-document... */
