css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("theonion.com") {
    /* Hide the related articles sidebar */
    .related-articles {
        display: none !important;
    }
    /* hide the shadow thing on the top */
    #shadow {
        display: none !important;
    }
    .main {
        width: auto !important;
    }
    .article-body {
        font-family: serif !important;
        font-size: 13pt !important;
    }
    .picture.title-picture {
        padding-right: 0 !important;
        border: 1px solid gray !important;
    }
    .share-tools.vertical-large {
        opacity: 0.2 !important;
        transition: opacity 0.4s ease;
        padding: 0 !important;
    }
    .share-tools.vertical-large:hover {
        opacity: 1 !important;
    }
    p.caption {
        background: #444 !important;
        color: #ddd !important;
        margin: 0 !important;
        padding: 5px !important;
    }
    /* Headline */
    .full-article h1, .full-article h2 {
        font-family: serif !important;
        background: rgba(0, 141, 82, 0.1);
        border-left: 5px solid rgb(0, 141, 82);
        padding-left: 5px;
        margin-bottom: 0 !important;
    }
    /* links below headline */
    .full-article .meta {
        margin-top: 0 !important;
        background: rgba(0, 141, 82, 0.2);
        border-left: 5px solid rgb(0, 141, 82);
        padding-left: 0 !important;
        padding-right: 0 !important;
    }
    .meta.pubdate {
        background: rgba(0, 141, 82, 0.3);
        color: #333;
    }
    .author {
        padding-left: 3px !important;
        opacity: 0.5;
        font-weight: normal !important;
    }
    /* ooh, colors for each tag! */
    .meta a {
        margin: 0 !important;
        padding: 3px !important;
    }
    .meta a:hover {
        border-bottom: 2px solid rgb(0, 141, 82) !important;
        text-decoration: none !important;
    }
    a.type[href="/features/news-in-brief/"]{
        background-color: #7af !important;
    }
    a.type[href="/features/sports-news-in-brief/"]{
        background-color: #f77 !important;
    }
    a.type[href="/features/slideshow/"]{
        background-color: #ee0 !important;
    }
    a.type[href="/features/news/"]{
        background-color: #0c0 !important;
    }
    a.type[href="/features/commentary/"]{
        background-color: #68d !important;
    }
    .meta a[href="/channels/breaking/"]{
        background-color: #f00 !important;
    }
}
