css:
@-moz-document domain(ec.europa.eu) {
html body #content {
width: 800px;
display: inline-block;
vertical-align: top;
margin-left: 30px;
}
html body div#Rahmen {
display: inline-block;
width: 150px;
height: auto;
padding: 0;
background-repeat: repeat;
}
html body ul#Navigation {
padding: 0;
}
html body ul#Navigation li {
float: none;
line-height: 31px;
}
html body ul#Navigation li a {
line-height: 31px;
height: 31px;
border: none;
}
html body ul#Navigation li ul {
width: auto;
top: 0;
left: 150px;
border: 1px solid #fff;
white-space: nowrap;
z-index: 1;
}
html body ul#Navigation li ul li a {
padding: 0em 0.8em 0em 0.8em;
}
html body #beyond-header {
color: #003366;
padding: 0.5em 1em 1em 1em;
float: none;
background: #93BA6F;
}
html body div#container-search-share {
width: 320px;
margin-right: 20px;
float: right;
margin-top: -60px;
}
}
