.screenshot > img {
    image-rendering:optimizeSpeed;             /* Legal fallback                 */
    image-rendering:-moz-crisp-edges;          /* Firefox                        */
    image-rendering:-o-crisp-edges;            /* Opera                          */
    image-rendering:-webkit-optimize-contrast; /* OSX Chrome/Safari              */
