css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("kingdomofloathing.com"), domain(kol.coldfront.net) {

input, textarea, select, option {-moz-appearance: none !important;}

* {background: #0c0c0c !important;}



 *:not(b):not([color]):not([style*="color:"]), font[color="black"]

{color: #0e2424 !important;}



[class*="utton"], input[type*="text"], td[style*="border: 1px solid"], .tborder, #ChatWindow, [style*="border: 1px solid"], #InputForm, div.rcm, textarea, td[bgcolor*="e"]

{border: 1px solid #17385d !important;}

td[bgcolor="blue"][width="50"] +td, td[bgcolor="blue"][width="50"]:first-child {border-right: none !important;}

td[bgcolor="blue"][width="50"] +td, td[bgcolor="blue"][width="50"]:last-child {border-left: none !important;}



 td[bgcolor*="e"] {border-bottom: none !important;}



img {opacity: 0.75 !important;}



td[bgcolor="#0c0c0c"], td[bgcolor="#0c0c0c"][height] {background: #17385d !Important;}



td[bgcolor="blue"], td[bgcolor="orange"], p.rcm:hover {background: #17385d url(data:image/png;base64,) center repeat-x !important; color: #FFF !Important;}



td[bgcolor="blue"] *, td[bgcolor="orange"] * {background: transparent !important;}



font[color]:not([color="white"]):not([color="#0c0c0c"]), font[color]:not([color="white"]):not([color="#0c0c0c"]) *, td[height][bgcolor], p.rcm:hover, input[type="text"], textarea {font-weight: bold !important;}



td[width$="0"][height$="0"]:empty {background: #17192d !important;}

#ChatWindow font[color="green"] {color: orange !important;}

#ChatWindow font[color="blue"] {color: white !important; font-weight: normal !important;}

}
