css:
@-moz-document domain("youtube.com") {
#body-container{background-color:#000 !important}
#page-container{color:#0f0;font-family:monospace !important}
#guide .appbar-guide-menu{color:#000 !important}
#page-container .guide-item{color:#0f0 !important}
#page-container .branded-page-v2-primary-col,#page-container .yt-card{background-color:#000;border:0 !important}
#page-container #results .pyv-promoted-videos{background-color:#333 !important}
#page-container .yt-alert-message,#page-container .filter-bar-container .num-results{color:#0f0 !important}
#page-container .filter-bar-container .num-results strong{color:#f00 !important}
#page-container #filter-dropdown{background-color:#000 !important}
#page-container #filter-dropdown .filter-col-title{color:#f00 !important}
#page-container #filter-dropdown .filter{color:#0f0 !important}
#page-container .promoted-videos .promoted-videos-syah,#page-container .promoted-videos .promoted-videos-helpcenter{color:#777 !important}
#page-container .yt-lockup{color:#0f0 !important}
#page-container .yt-lockup .yt-lockup-title{font-size:12px !important}
#page-container .yt-lockup .yt-user-name{color:#ff0 !important}
#page-container .yt-lockup .yt-lockup-description a{color:#2793e6 !important}
#page-container .yt-lockup .yt-badge{color:#fff !important}
#page-container #search-results{padding:0 20px 20px 30px !important}
#page-container .geek-week-layout-header{padding-bottom:20px !important}
#page-container .geek-week-layout-result-item{font-size:12px;color:#fff;padding-bottom:10px !important}
#page-container .geek-week-layout-result-item .geek-week-username a{color:#ff0 !important}
#page-container .geek-week-layout-result-item .geek-week-title{font-weight:bold;font-size:14px !important}
#page-container .geek-week-layout-result-item.video .geek-week-title a{color:#2793e6 !important}
#page-container .geek-week-layout-result-item.playlist .geek-week-title a{color:#0ff !important}
#page-container .geek-week-layout-result-item.channel .geek-week-title a{color:#ff8c00 !important}
#page-container .geek-week-layout-result-item .geek-week-description{padding-left:25px !important}
#page-container .geek-week-layout-result-item .geek-week-description a{color:#fff !important}
#page-container .search-header{visibility:hidden !important}
#page-container .search-pager .yt-uix-button{border:initial;box-shadow:initial !important}
#page-container .search-pager .yt-uix-button-default{background:initial;border-color:initial;color:#f0f !important}
#page-container .search-pager .yt-uix-button-toggled{color:#fff !important}
}
