css:
@-moz-document domain("comunio.es")
{
  *
  {
      font-family:Arial Black;
  }
  body
  {
      background:#555555 !important;
  }
  form div.bar, .bar
  {
      background:#cccccc !important;
  }
  h1 > br, #title br
  { 
      display:none !important;
  } 
  h1 > span
  { 
      margin:0px 5px 0px 0px !important;
  } 
  #edgenavi .top b, .edgeg .top b, .edgetitle .top b, .titleboxcontent, .boxcontentdown, #language, #newsfilter, #manager, #subnavititle, #id, .infoBox tr, #openid, #balance, #user, .tablecontent03, .tablecontent06, #tipgame, #tablestandings, #tableforum 
  {
      background-color: #111111;
  }
  #advertising-top, #advertising-right, #smallcontentrightex, #socialnetworks, #playerstatus, #header_dd_cont, #str_rectangleAd, #rectangleAd, #portletFB
  {
      display:none !important;
  }
  #center
  {
      width:830px !important;
  }
  .notOnly468
  {
    background: url(http://static.tumblr.com/51431ba9b71f3872726b592132a7408c/guynlby/cMUmnho10/tumblr_static_gray_background_1440x900_by_ktaylor2424-d4xmmg4.png) no-repeat center center fixed !important;
    -webkit-background-size: cover !important; 
    -moz-background-size: cover !important;    
    -o-background-size: cover !important;      
    background-size: cover !important;         
  }
  #contentleftex
  {
      width:810px;
      margin:0px 10px;
  }
  .article_header1, .article_header2, .article_header3 
  {
      border-radius: 0px 15px 0px 0px;
      background:#cccccc;
  }
  .article_content1, .article_content2, .article_content3
  {
      border-radius: 0px 0px 0px 15px;
      background:#eeeeee;
  }
  .edgetitle .top, .edge1 .top, .edge2 .top, .edge3 .top, #content, #footer, #topib
  {
      background-color: #555555;
  }
  .tr1 
  {
      background-color: #cccccc;
      color: #002000;
  }
  .tr1 td
  {
      background-color: #cccccc !important;
  }
  .tr2
  {
      background-color: #eeeeee;
      color: #002000;
  }
  #top, #topib
  {
      height: 68px;
      background:#555555;
  }

  #mainmenu-con, #menuib {
      background-color: #185412;
      background-image: linear-gradient(top, rgb(90,90,90) 0%, rgb(0,0,0) 100%);
      background-image: -o-linear-gradient(top, rgb(90,90,90) 0%, rgb(0,0,0) 100%);
      background-image: -moz-linear-gradient(top, rgb(90,90,90) 0%, rgb(0,0,0) 100%);
      background-image: -webkit-linear-gradient(top, rgb(90,90,90) 0%, rgb(0,0,0) 100%);
      background-image: -ms-linear-gradient(top, rgb(90,90,90) 0%, rgb(0,0,0) 100%);
      background-image: -webkit-gradient( linear, left top, left bottom, color-stop(0, rgb(90,90,90)), color-stop(1, rgb(0,0,0)) );
  }
  ul#mainnav li .subnavi, .mainnavactiv, .mainnavactiv a, .mainnavactiv a:hover, ul#mainnav .subnavi ul li:hover, ul#mainnav .subnavi:hover, ul#mainnav li .subnavi:hover, ul#mainnav li *:hover, #playactiv
  {
      background: #cccccc !important;
      background-color: #cccccc !important;
  }
  .mainnav a:hover,.mainnavitem a:hover, #stats,#play,#predict,#cup,#gameinfo,#shop,#forum,#links, #statsactiv, #playactiv, #predictactiv, #cupactiv, #gameinfoactiv, #shopactiv, #forumactiv, #linksactiv 
  {
      background-color:#cccccc;
      color:#002000;
  }
  ul#mainnav li .subnavi ul, ul#mainnav li .subnavi ul:hover, ul#mainnav li .subnavi ul *, ul#mainnav li .subnavi ul *:hover, .mainnavactiv a, .mainnavactiv a:hover, .contenttext
  {
      background:#cccccc !important;
  }
  #menushadowib
  {
      margin:0px !important;
  }
  #title
  {
      height:auto !important;
      padding:5px;
  }
}
