css:
@-moz-document domain("duckduckgo.com"), domain("ddg.gg") {
/*
        Lightweight style for DuckDuckGo website

        based on
            DuckDuckGo Elementary (http://userstyles.org/styles/94275/duckduckgo-elementary)
            DuckDuckGo Hide the duck (http://userstyles.org/styles/62637/duckduckgo-hide-the-duck)
        with several fixes

        Source code:
        http://userstyles.org/styles/98169/duckduckgo-lightweight
        https://github.com/sbriskin/userstyles.org/duckduckgo-lightweight

    */
    .logo_homepage {
        background: url("data:image/png;base64,") no-repeat scroll center top transparent !important;
        height: 50px !important;
        margin-top: 30px !important;
        margin-bottom: 40px !important;
        padding-bottom: 0 !important;
        width: 310px !important;
    }
    #logo {
        display: none !important;
    }
    body, .highlight, #side {
        background: #FAFAFA !important;
    }
    #header_button_wrapper, #side_powered_by_wrapper, #side .button {
        display: none !important;
    }
    body {
        font-family: 'Open Sans', 'Droid Sans', arial, sans-serif!important;
    }
    a {
        color: #474747 !important; /* in retro style: #2f82c2 */
    }
    a:visited {
        color: #09AD00 !important; /* in retro style: #6932BB */
    }
    a.url, .result__url {
        color: #A55959 !important;
    }
    #tagline_homepage a {
        color: #A55959 !important;
    }
    /*
        Removed code for old elements
     */
}
