css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('developpez.com'), domain('developpez.net') {

/* reset some css */

body a, #forums a.username {
    color: rgb(40, 40, 40);
}

.forumbit_post .forumrow, .forumhead + .childforum .L2:first-child .forumrow, .forumhead + .L2 .forumrow {
    color: black;
}

.title {

    font-size : 1.1em;

}

.toolsmenu .popupgroup a.popupctrl, 
.toolsmenu .popupgroup .popupmenu a.popupctrl, 
.toolsmenu .popupgroup .popupmenu:hover a.popupctrl, 
.toolsmenu .popupgroup .popupmenu:hover 
.popupctrl a.popupctrl.active {
    border: 0px none;
    color: rgb(78, 78, 78);
    padding-right: 15px;
    font-weight: bold; /* add bold */
}

* {

box-shadow: none;

}

/*header*/

#quicknav {
    width: 935px;
    height: 25px;
    margin: auto;
    font-family: Verdana,sans-serif;
    background:  rgb(61, 102, 97);
    border-radius: 5px;
}

#quicknav ul {
    list-style-type: none;
    height: 25px;
    padding: 0px 15px;
    background:  transparent;
    line-height: 17px;
   
}

#quicknav li a {
    text-decoration: none;
    color : rgb(255,255,255);
    font-weight: bold;
    font-size: 12px;
}

#quicknav #quicksearch form {
    background: rgb(61, 102, 97);
    height: 20px;
   /* padding: 0px 5px 5px 0px;*/
    width: 195px;
    border-radius : 5px;
}

/*--------------*/

/* threah head */

.threadlisthead {
    font: bold 12px Tahoma,Calibri,Verdana,Geneva,sans-serif;
    margin: 0px;
    width: 100%;
    background: rgb(61, 102, 97);
    color: rgb(255, 255, 255);
    float: left;
    display: block;
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
    box-shadow: none;
    position: relative;
}


/* ----------- */

html {
    background: none repeat scroll 0% 0% rgb(225, 225, 226);
}

span {
    border-top: 0px;
    border-left: 0px;
}

.globalsearch .buttoncontainer {
    height: 26px;
    border-left: 0px;
    display: inline-block;
    border-right: 0px;
    width: 25px;
    overflow: hidden;
    float: right;
}

.globalsearch {
    text-align: right;
    line-height: 0;
}

.globalsearch .textboxcontainer span {
    border-top: 0px;
    border-left: 0px;
    display: inline-block;
    height: 22px;
    padding-left: 2px;
    padding-right: 2px;
    padding-top: 3px;
    margin-bottom: 1px;
    width: 147px;
}
.globalsearch .textboxcontainer {
    text-align: center;
}
.globalsearch {
    text-align: right;
    line-height: 1em;
}

.navtabs {
    background: rgb(61, 102, 97);
    padding: 0px 0px 0px 20px;
    height: 26px;
}

#shadow1 {
    padding-left: 3px;
    background: rgb(194, 193, 165);
}

#shadow2 {
    padding-right: 3px;
    background:  rgb(194, 193, 165);
}

.above_body {
    margin: 10px 20px;
    height: 70px;
    background: none repeat scroll 0% 0% rgb(117, 148, 131);
    border-radius: 5px;
    box-shadow: none;
}

body:not(iframe) {
    background: none repeat scroll 0% 0% rgb(225, 225, 226);
    margin: 0px;
}

body.forum{ /* iframe de m$$rde */
    background: none repeat scroll 0% 0% rgb(225, 225, 226);
    margin: 0px;
    margin-left: 2px;
    margin-right: 2px;

}

#gabarit_tabmenu {
    height: 31px;
    margin: 0px;
    background-color: rgb(61, 102, 97) !important;
    background-image: none;
    background-repeat: repeat-x;
}

#gabarit_tabmenu #tabs li:hover a {
    background-image: none;
    background-color: rgb(29, 53, 61);
    background-repeat: repeat-x;
    color: rgb(255,255,255);
}

#gabarit_tabmenu #tabs li a.selected, #gabarit_tabmenu #tabs li a.selected:hover {
    background-image: none;
    background-repeat: repeat-x;
    background-color: rgb(29, 53, 61);
    color: rgb(255,255,255);
}

#gabarit_tabmenu #tabs {
    background-image: none;
    background-repeat: no-repeat;
    padding-left: 0.75em;
    height: 31px;
}

#gabarit_tabmenu #tabs li {
    float: left;
    position: relative;
    display: block;
    font-size: 12px;
    line-height: 34px;
    color: rgb(216, 215, 215);
    z-index: 100;
    height: 30px;
    background-image: none;
    background-repeat: no-repeat;
}

#gabarit_tabmenu div {
    background-image: none;
    background-repeat: no-repeat;
    float: right;
    height: 31px;
    width: 16px;
}

#gabarit_tabmenu {
    height: 31px;
    margin: 0px;
    /*background-color: rgb(74, 99, 143);*/
    background-image: none;
    background-repeat: repeat-x;
    border-radius: 10px 10px 0px 0px;
}

.forumhead {
    background:  rgb(61, 102, 97);
    padding: 0px;
    color: rgb(255, 255, 255);
    clear: both;
    margin-top: 8px;
    float: left;
    width: 100%;
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
    border: 1px solid rgb(90, 127, 151);
    font: bold 14px Arial,Tahoma,Calibri,Verdana,Geneva,sans-serif;
}

#gabarit {
    clear: both;
    font-family: Verdana,sans-serif;
   /* background-color: rgb(168, 154, 112);*/
}

#gabarit_resourcemenu #resources li {
    display: inline-block;
    height: 15px;
    vertical-align: text-bottom;
    padding-left: 8px;
    background:  repeat-x scroll 0px 0px rgb(117, 148, 131);
    border-radius: 1px;
    border: 1px solid rgb(90, 127, 151);
    margin-top: 2px;
}

#gabarit_resourcemenu_left {
    background-image: none;
    background-repeat: repeat-y;
    float: left;
    height: 30px;
    width: 16px;
}

#gabarit_resourcemenu_right {
    background-image: none;
    background-repeat: repeat-y;
    float: right;
    height: 30px;
    width: 16px;
}

#gabarit_resourcemenu {
    height: 30px;
    text-align: center;
    margin: 0px;
    clear: left;
    background-image: none;
    background-repeat: repeat-x;
    background-color: rgb(168, 154, 112);
}

/* topic menu bar */

#gabarit_topicmenu {
    height: 32px;
    margin: 0px;
    font-size: 14px;
    clear: left;
    background-image: none;
    background-repeat: repeat-x;
    background-color : rgb(117,148,131);
}

#gabarit_topicmenu div {
    background-image: none;
    background-repeat: no-repeat;
    float: right;
    height: 32px;
    width: 26px;
}

#gabarit_topicmenu #topics {
    background-image: none;
    background-repeat: no-repeat;
    padding-left: 5px;
    text-align: center;
    height: 32px;
}


#gabarit_topicmenu #topics li span {
    display: inline-block;
    height: 22px;
    padding-left: 8px;
    margin-top: 4px;
    background-color: rgb(29, 63, 51);
    border: 1px solid rgb(61, 102, 97);
}

#gabarit_topicmenu #topics li.selected span {
    background-image : none;
    background-color : white; 
}

#gabarit_topicmenu #topics li:hover span {
    background-image : none;
    background-color : white; 
}

/* --------------- */

.threadbit .alt {
    background: none repeat scroll 0% 0% rgb(117, 148, 131);
    color : white;
}

.toolsmenu {
    background: rgb(61, 102, 97);
    width: 100%;
    display: block;
    border: 1px solid rgb(206, 206, 206);
    float: left;
    clear: both;
    margin: 0.5em 0px;
    border-radius : 5px;
}

.toolsmenu .popupgroup a.popupctrl, 
.toolsmenu .popupgroup .popupmenu a.popupctrl, 
.toolsmenu .popupgroup .popupmenu:hover a.popupctrl, 
.toolsmenu .popupgroup .popupmenu:hover .popupctrl a.popupctrl.active {
    border: 0px none;
    color: rgb(255,255,255);
    padding-right: 15px;
}


/* boutons */


.newcontent_textcontrol {
    background:  rgb(61, 102, 97);
    border-radius: 5px;
    box-shadow: none;
    font: 14px Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 4px 15px;
    border: 0px none;
    display: block;
    /*float: left;*/
    clear: right;
}

.globalsearch .buttoncontainer {
    height: 26px;
    border-left: 0px;
    display: inline-block;
    border-right: 0px;
    width: 25px;
    overflow: hidden;
    float: right;
}

.globalsearch .buttoncontainer span {
    border-top: 0px;
    border-left: 0px;
    height: 23px;
    padding-left: 4px;
    padding-top: 2.5px;
    display: inline-block;
}


/* ------- */



/* message post */

.postbitlegacy .postdetails, .eventbit .eventdetails {
    float: left;
    width: 100%;
    /*background:none repeat scroll 0% 0% rgba(117, 148, 131, .2); */
}

.postbitlegacy .userinfo {
    float: left;
    position: relative;
    width: 180px;
     padding: 2px 10px 5px; 
    font-size: 11px;
    display: block;
   /* background: none repeat scroll 0% 0% rgba(117, 148, 131, .4); */
   /* clear: both;*/
    height : 100%;
    
}

.postbitlegacy .postbody, .eventbit .eventdetails .eventbody {
    background: none repeat scroll 0% 0% rgb(250, 250, 250);
    /*margin-left: 200px; */
    border-left: 2px solid rgba(61,102,97,.6);
    padding-bottom: 1em;
}


.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
    background:  rgb(29, 53, 61);
    border: 1px solid rgb(50, 91, 105);
    clear: both;
    display: block;
    float: left;
    width: 100%;
    color: rgb(255, 255, 255);
    margin: -1px -1px 0px;
    font: 12px Tahoma,Calibri,Verdana,Geneva,sans-serif;
    padding: 4px 0px;
}

.posts, #message_list {
    /*margin: 10px 100px;*/
}

#thread_controls {
    float: none;
    width: auto;
    margin-right: 320px;
    height: 30px;
   /* margin-left: 100px; */
}


/* -----------  */

/* general head */


.forum_info .blockhead {
    font-size: 12px;
    font-weight: bold;
    color: rgb(255, 255, 255);
    background: rgb(61, 102, 97);
    border: 1px solid rgb(96, 96, 96);
    margin-top: 5px;
    box-shadow: none;
}



/* ------------ */


/* footer */

.footer {
    clear: both;
    margin-top: 15px;
    text-align: right;
    display: block;
    box-shadow: none;
    background: none repeat scroll 0% 0% rgb(61, 102, 97);
    padding: 5px 0px;
    border: 1px solid rgb(66, 87, 105);
    font: 13px Tahoma,Calibri,Verdana,Geneva,sans-serif;
}

#gabarit_footer #gabarit_footer_left {
    background-image: none;
    background-repeat: no-repeat;
    background-position: left center;
    float: left;
    width: 16px;
    height: 33px;
}

#gabarit_footer {
    padding: 0px;
    margin: 0px 0px 2px;
    background-image: none;
    background-repeat: repeat-x;
    background-position: left bottom;
    background: none repeat scroll 0% 0% rgb(61, 102, 97);
    font-family: Verdana,sans-serif;
    height: 33px;
}

#gabarit_footer #gabarit_footer_right {
    background-image: none;
    background-repeat: no-repeat;
    background-position: left center;
    float: left;
    width: 16px;
    height: 33px;
    color : white !important;
}

#gabarit_pied #gabarit_pied_right {
    background-image: none;
    background-repeat: repeat-y;
    background-position: right center;
    float: right;
    width: 14px;
    height: 100%;
}

#gabarit_footer #responsable {
    display: block;
    font-size: 8pt;
    text-align: center;
    color: white;
    padding-top: 10px;
}

#gabarit_pied {
    padding: 0px;
    margin: 0px;
    background-image: none;
    background-repeat: repeat-x;
    background-position: left top;
    background-color : rgb(61, 102, 97);
    height: 142px;
}

#gabarit_footer a {
    color: white;
}

#gabarit_pied #gabarit_pied_left {
    background-image: none;
    background-repeat: repeat-y;
    background-position: left center;
    float: left;
    width: 16px;
    height: 142px;
}


/* ---------- */


/* pagination bubble */


.pagination span.selected a {
    background:  rgb(61, 102, 97);
    color: rgb(255, 255, 255);
    border: 1px solid rgb(65, 115, 148);
    height: 20px;
    font-weight: bold;
    padding: 2px 4px;
    margin-left: 2px;
    border-radius: 10px;
}
.pagination span a {
    font: 13px Tahoma,Calibri,Verdana,Geneva,sans-serif;
    border: 1px solid rgb(206, 223, 235);
    height: 20px;
    padding: 2px 4px;
    margin-left: 2px;
    background: none repeat scroll 0% 0% rgb(242, 246, 248);
    text-decoration: none;
    color: rgb(65, 115, 148);
    border-radius: 10px;
}


/* ---------------- */


/* header search */

h2.searchlisthead {
    font: bold 12px Tahoma,Calibri,Verdana,Geneva,sans-serif;
    background:  rgb(61, 102, 97);
    color: rgb(255, 255, 255);
    float: left;
    display: block;
    width: 100%;
    position: relative;
    clear: both;
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
    box-shadow: none;
}

/* ------------- */


/* tableau de bord */

.blockhead {
    font: 12px Tahoma,Calibri,Verdana,Geneva,sans-serif;
    color: rgb(255, 255, 255);
    background: rgb(61, 102, 97);
    padding: 4px 10px;
    border: 1px solid rgb(90, 127, 151);
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
}

/* ----------------- */


}
