css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://localhost:8888/"), 
               url-prefix("http://127.0.0.1:8888/"),
               url-prefix("http://localhost:8889/"),
               url-prefix("http://localhost:8890/")
{
    div.cell.selected {
    background: linear-gradient(to right, #337ab7 -40px, #337ab7 5px, transparent 5px, transparent 100%);
    }
#site {
    background: #101010;
}
.terminal-app #header {
    background: #000;
}
    .typeahead-container button:focus, .typeahead-container button {
    background-color: #999;
    border-color: #333;
    }
    .typeahead-dropdown > li.active > a, .typeahead-list > li.active > a, .typeahead-dropdown > li > a:focus, .typeahead-dropdown > li > a:hover, .typeahead-list > li > a:focus, .typeahead-list > li > a:hover {
    background-color: #1b1b1b;
    color: #ccc;
    }
    .typeahead-dropdown > li > a, .typeahead-list > li > a {
    background-color: #161616;
    color: #ccc;
    }
    .typeahead-dropdown, .typeahead-list {
    background-color: #161616;
    }
    .typeahead-list > li.typeahead-group.active > a, .typeahead-list > li.typeahead-group > a, .typeahead-list > li.typeahead-group > a:focus, .typeahead-list > li.typeahead-group > a:hover {
    border-color: #333;
    color: #8899a6;
    background-color: #242424;
    }

div#pager {
    background-color: #101010;
    }

div#pager .ui-resizable-handle {
    background: #333;
    border-top: 1px solid #333;
    border-bottom: 1px solid #333;
    }

    div#pager .ui-resizable-handle::after {
    border-top: 1px solid #999;
    }
    
#texteditor-backdrop {
    background-color: #101010;
}

body > #header {
    display: none;
    background-color: #050505;
}

body > #header .header-bar {
    background: #101010 none repeat scroll 0% 0%;
}

#texteditor-backdrop #texteditor-container {
    background-color: /*[[bc]]*/;
}

#texteditor-backdrop #texteditor-container .CodeMirror-gutter, #texteditor-backdrop #texteditor-container .CodeMirror-gutters {
    background-color: /*[[bc]]*/;
}

    .modal-content {
    background-color: #050505;
}
    
    span.save_widget span.filename:hover {
    background-color: #333;
}

    .notebook_app #header {
    box-shadow: 0px 0px 1px 1px rgba(87, 87, 87, 0.2);
}
    
    .notebook_app {
    background-color: #050505;
}
    
    .navbar-default {
    background-color: rgb(26,26,26) !important;
    background-image: linear-gradient(to bottom, #242424, #101010) !important;
    border-color: #E7E7E7;
}
    
    #header .header-bar {
    width: 0px;
    background: none repeat scroll 0% 0% #666;
}
    
    .caret {
    border-top: 4px solid !important;
    border-right: 4px solid transparent !important;
    border-left: 4px solid transparent !important;
}
    
    .list_item:hover {
    background-color: #050505;
}
    
    .list_header {
    font-weight: bold;
    background-color: #050505;
}
    
    .list_container > div {
    border-bottom: 1px solid #666;
}
    
    #header {
    background-color: #050505;
}
    
    body {
        background-color: rgb(16,16,16);
        color: #BBB !important;
    }
    .breadcrumb > li {
        text-shadow: 0px 0px 0px #FFF!important;
        color: #BBB !important;
    }
    .btn {
        color: #BBB !important;
        text-shadow: 0px 0px 0px rgba(255, 255, 255, 0.75) !important;
        background-color: rgb(26,26,26) !important;
        background-image: linear-gradient(to bottom, #242424, #101010) !important;
    }
    * {
        border-color: rgb(36,36,36) !important;
    }
    .nav-tabs > .active > a, .nav-tabs > .active > a:hover, .nav-tabs > .active > a:focus {
        color: #BBB !important;
        background-color: #060606 !important;
    }
    .nav > li > a:hover, .nav > li > a:focus {
        background-color: #060606 !important;
    }
    div.input_prompt {
        color: #08C !important;
        border-top: 1px solid transparent !important;
    }
    .navbar-inner {
        background-color: #242424 !important;
        background-image: linear-gradient(to bottom, #242424, #101010) !important;
    }
    .navbar .nav > li > a {
    color: #BBB !important;
    text-shadow: 0px 0px 0px #FFF !important;
    }
    div.output_area pre {
    color: #BBB !important;
}
    select {
    background-color: #101010 !important;
}
    select, textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
    color: #BBB !important;
}
    .dropdown-menu {
    background-color: #101010 !important;
}
    .dropdown-menu > li > a {
    color: #BBB !important;
}
    .dropdown-menu .divider {
    background-color: #BBB !important;
}
    .navbar .nav li.dropdown.open > .dropdown-toggle, .navbar .nav li.dropdown.active > .dropdown-toggle, .navbar .nav li.dropdown.open.active > .dropdown-toggle {
    background-color: #060606 !important;
}

    div.out_prompt_overlay:hover {
    background: none repeat scroll 0% 0% rgba(6,6,6, 0.5) !important;
}
    .completions {
        box-shadow: none !important;
}
    
.ipython_tooltip {
    background-color: #101010 !important;
    box-shadow: none !important;
}
    .ipython_tooltip .tooltiptext pre {
    background-color: #101010 !important;
    color: #BBB !important;
}
    .pretooltiparrow:before {
    background-color: #101010 !important;
}

.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    background-color: #262626 !important;
    color: #BBB !important;
}
div#pager pre {
    color: #BBB !important;
    background-color: #101010 !important;
}

    div.output_prompt {
    color: #FF4E4E !important;
}

    .notification_widget {
    color: #BBB !important;
    background: none repeat scroll 0% 0% rgba(16, 16, 16, 0.5) !important;
}
    .modal {
    background-color: #101010 !important;
}
 
    .modal-footer {
    background-color: #101010 !important;
    box-shadow: 0px 1px 0px #101010 inset !important;
}
    textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
    background-color: #101010 !important;
}
    
    .ui-icon {
    background-color: #BBB !important;
}
    
/* 20140214 */
    
    .MathJax span {
    border-color: #BBB !important;
}
    

    .navbar-text {
    color: #BBB !important;
}
    
    div.output_stderr {
    background: none repeat scroll 0% 0% #643939 !important;
}

/* 20140406 */
    
.rendered_html {
    color: #BBB !important;
}       

/* 20141204 */

.ui-state-hover, .ui-widget-content .ui-state-hover, .ui-widget-header .ui-state-hover, .ui-state-focus, .ui-widget-content .ui-state-focus, .ui-widget-header .ui-state-focus {
    border: 1px solid #333 !important;
    background: none #060606 !important;
}    

.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    border: 1px solid #333 !important;
    background: none;
}        
  
    .dropdown-menu .divider {
    background-color: #666 !important;
}
    
    .dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
    background-color: #050505;
}

    .panel {
    background-color: #111;
}    
    
    #running .panel-group .panel .panel-heading {
    background-color: #060606;
}





/* color theme */    


/* input area */
    
    /* background color */
    
    div.input_area {
    background: none repeat scroll 0% 0% /*[[bc]]*/ !important;
}
    
    /* basic font color */
    
    .CodeMirror {
    color: /*[[bfc]]*/ !important;
}
    
    /* selected text background color */
    
    .CodeMirror-selected {
    background: none repeat scroll 0% 0% /*[[tsbc]]*/ !important;
}
    
    /* cursor color */
    
    .CodeMirror div.CodeMirror-cursor {
    border-left: 1px solid /*[[cc]]*/ !important;
}
    
    /* keyword color */
    
    .cm-s-ipython span.cm-keyword {
    color: /*[[kc]]*/ !important;
}
    
    /* variable color */
    
    .cm-s-ipython span.cm-variable {
    color: /*[[vc]]*/ !important;
}
    
    .cm-s-ipython span.cm-variable-2 {
    color: /*[[vc]]*/;
}

    /* string color */
    
    .cm-s-ipython span.cm-string {
    color: /*[[sc]]*/ !important;
}
    
    .cm-s-ipython span.cm-link {
    color: /*[[sc]]*/;
}

    /* comment color */
    
    .cm-s-ipython span.cm-comment {
    color: /*[[cmc]]*/ !important;
}

    .cm-s-ipython span.cm-header {
    color: /*[[cmc]]*/;
}

    /* built in function color */
    
    .cm-s-ipython span.cm-builtin {
    color: /*[[bifc]]*/ !important;
}

    /* local function color */
    
    .cm-s-ipython span.cm-def {
    color: /*[[lfc]]*/;
}
    
    /* number color */
    
    .cm-s-ipython span.cm-number {
    color: /*[[nc]]*/ !important;
}
    
    /* operator color */
    
    .cm-s-ipython span.cm-operator {
    color: /*[[oc]]*/ !important;
}   
    
    /* matching bracket color */
    
    div.CodeMirror span.CodeMirror-matchingbracket {
    color: /*[[bfc]]*/ !important;
    background-color: /*[[mbc]]*/ !important;
}
    
    /* error syntax color */
    
    .cm-s-ipython span.cm-error {
    color: /*[[esc]]*/ !important;
}
    
    .CodeMirror-gutters {
    border-right: 1px solid #DDD;
    background-color: #242424 !important;
    white-space: nowrap;
}
    
/* output area */
    
    /* output background color */
    
    #notebook-container {
    background-color: #101010;
    box-shadow: 0px 0px 1px 1px rgba(87, 87, 87, 0.2);
}

    /* selected region frame color */

    div.cell.selected {
    border: 1px solid #234 !important;
    margin: -1px !important;
}    

    div.cell {
    border: 0px solid #234 !important;
}
    .ansicyan {
    color: #FFA0A0;
}
    
    .ansiyellow {
    color: #FFA0A0 !important;
}
    
    .ansigreen {
    color: #F0E68C !important;
}
    .ansiblue {
    color: #87ceeb !important;
}
    
    .ansired {
    color: #FF4E4E !important;
}
    


    /* Markdown Cell */
        .CodeMirror {
    background: #333;
    color: #DDDDDD;
    }
    .CodeMirror-gutters {
    background-color: #444;
    }
    .CodeMirror-selected {
    background: none repeat scroll 0% 0% #101010;
}
    .CodeMirror-cursor {
    border-left: 1px solid #AAAAAA !important;
}
    div.CodeMirror span.CodeMirror-matchingbracket {
    color: #DDDDDD !important;
    background-color: #008B8B;
}
    #size_current {
    color: #ccc !important;
    }
.cm-s-default .cm-qualifier {
    color: #ddd;
    }
    .cm-s-default .cm-atom {
    color: #FFA0A0;
    }
        .cm-s-default .cm-keyword {
    color: #F0E68C !important;
}
    .cm-s-default .cm-variable {
    color: #DDDDDD !important;
}
    .cm-s-default .cm-attribute {
    color: #F0E68C;
    }
    .cm-s-default .cm-variable-2 {
    color: #DDDDDD;
}
.cm-s-default .cm-variable-3 {
    color: #DDDDDD;
}
    .cm-s-default .cm-string {
    color: #FFA0A0 !important;
}
    .cm-s-default .cm-link {
    color: #FFA0A0;
}
    .cm-s-default .cm-comment {
    color: #87CEEB !important;
}
    .cm-s-default .cm-header {
    color: #87CEEB;
}
    .cm-s-default .cm-builtin {
    color: #98FB98 !important;
}
    .cm-s-default .cm-def {
    color: #98FB98;
}
    .cm-s-default .cm-number {
    color: #FFA0A0 !important;
}
    .cm-s-default .cm-operator {
    color: #FFA858 !important;
}   
    .cm-s-default .cm-error {
    color: #FF5555 !important;
}
.cm-s-default .cm-tag {
    color: #F0E68C;
    }
    span.line-num {
    background: #333;
}
    .CodeMirror-scrollbar-filler, .CodeMirror-gutter-filler {
    background-color: #333;
    }
    .rendered_html pre, .rendered_html code {
    background-color: #333;
    color: #ddd;
    }
    


}
