{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer, fieldset

{
color: white !important;
background: black !important;
}

select, button, html, head, body, footer, .content, #footer-container, #widget_bounds div, .Lub div, .IPc, .SPc div, .QPc div, #content div, #yt-masthead-container

{
background: black !important;
}

span

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
