css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("basecamp.com") {
    section.comments article.comment.new.expanded, section.comments article.comment.edit.expanded {
        border-radius: 0px;
    }

    .text_entry {
        border-radius: 0px;
    }

    [data-behavior~="wysiwyg_toolbar"] > a:last-of-type {
        border-top-right-radius: 0px;
        border-bottom-right-radius: 0px;
    }

    [data-behavior~="wysiwyg_toolbar"] > a:first-of-type {
        border-top-left-radius: 0px;
        border-bottom-left-radius: 0px;
    }

    form div.attachments {
        border-radius: 0px;
    }

    .action_button {
        border-radius: 0px;
        box-shadow: none;
    }

    .action_button.green {
        box-shadow: none;
    }

    div.sheet {
        box-shadow: none;
        border: 1px solid rgba(0, 0, 0, 0.3);
        border-radius: 0px;
    }

    div.stack_container > div.sheet.inactive {
        border: 1px solid rgba(0, 0, 0, 0.1);
    }

    div.jumpto input[type="text"],
    div.jumpto input[type="text"]:focus {
        box-shadow: none;
        border: 1px solid #4B80C8;
    }
    div.jumpto input[type="text"] {
        border-radius: 0px;
        border: 1px solid rgba(0, 0, 0, 0.3);
    }

    img.avatar {
        border-radius: 0px;
    }

    article.project_card {
        border: 1px solid rgba(0, 0, 0, 0.3);
        border-radius: 0px;
        box-shadow: none;
    }

    #image_enlarger div.image_wrapper aside a.button {
        border-radius: 0px;
    }

    div.edit_taggings {
        border: 1px solid #DEDEDE;
        border-radius: 0px;
    }

    .balloon {
        border: 1px solid #BBB;
        border-radius: 0px;
        box-shadow: none;
    }

    div.edit_taggings div.expanded_content input[type="text"] {
        border-radius: 0px;
        box-shadow: none;
    }

    #image_enlarger div.image_wrapper {
        border-radius: 0px;
    }
}
