css:
@-moz-document url-prefix("http://git-scm.com/book"), url-prefix("https://git-scm.com/book") {

    /*  ProGit Reader                                           */
    /*  http://userstyles.org/styles/98225/progit-book-reader   */
    /*  https://github.com/sbriskin/userstyles.org              */

    body {
        font-family:"Helvetica Neue", Helvetica, Arial, sans-serif;
        background: #F5F5F5 !important;
        color: #333 !important;
        line-height:135% !important;
    }
    .more {
        font-size: 0.7em;
    }
    img.right {
        float: right;
        margin: 15px;
    }
    a {
        color: #3E423A;
    }
    a:visited {
        color: #6C6E58;
    }
    #wrapper {
        max-width: 50em !important;
        margin: 2em auto 0;
        padding: 0;
        background: #fff;
        -moz-border-radius: 1.2em;
        -moz-box-shadow:0px 2px 2em rgba(0, 0, 0, 0.3);
    }
    hr {
        border-collapse:collapse;
        border: 1px dotted #eee !important;
    }
    #header {
        font-family:sans !important;
        text-align: left !important;
        display:none;
        background: #666 !important;
        -moz-border-radius-topleft: .51em;
        -moz-border-radius-topright: .51em;
        padding: 1em 1.5em !important;
        text-align: right !important;
    }
    #header h1 {
        font-size: 2em !important;
        display: inline;
        color: #aaa !important;
        font-weight: normal;
    }
    #header h2 {
        display: inline;
        margin-left: 0.5em;
        font-size: 1.4em;
        font-weight: normal;
        color: #aaa;
        display:none !important;
    }
    #menu {
        background: #444;
        display:none !important;
    }
    #menu ul {
        list-style: none;
        margin: 0;
        text-align: center;
    }
    #menu ul li {
        display: inline;
    }
    #menu ul li a {
        color: white;
        font-size: 1.1em;
        padding: 0 0.8em;
        line-height: 2.5em;
        font-weight: normal;
    }
    #message {
        padding: 20px;
        margin: 10px 30px;
        border: 1px solid #383;
        background: #aea;
        text-align: center;
    }
    #content {
        padding: auto;
        width: 100%;
    }
    #content h2 {
        margin: 0 0 0.5em 0;
        color: #666 !important;
        font-size: 1.5em;
    }
    #content h3 {
        color: #777 !important;
        padding-top:2em !important;
    }
    #content p {
        line-height: 150%;
    }
    span.date {
        background: #484;
        display: block;
        padding: 0.4em 0.6em 0.3em;
        font-weight: bold;
        color: white;
        border-bottom: 3px solid #262;
        float: left;
    }
    div.post {
        margin-bottom: 2em;
    }
    .post h1 {
        margin-top: 0.2em;
    }
    .post {
        color: #333;
    }
    .post h1 a {
        text-decoration: none;
        clear: left;
        display: block;
        color: black;
        padding: 0.3em 0;
        margin: 0 0 0.2em 0;
        border-bottom: 1px dashed #555;
        font-weight: bold;
    }
    .post br {
        margin-bottom: 10px;
    }
    pre {
        background: #333 !important;
        padding: 1em;
        -moz-border-radius:.5em !important;
        overflow: auto;
        color: white !important;
        line-height: 120%;
        font-family:"Consolas", "Courier New", Courier, mono;
        font-size: 12px;
        letter-spacing: 0;
        border: 0px none transparent !important;
    }
    pre>code, .man-page p tt, .man-page .listingblock .content {
        -webkit-border-radius: 3px;
        -moz-border-radius: 3px;
        -ms-border-radius: 3px;
        -o-border-radius: 3px;
        border-radius: 3px;
        display: block;
        padding: 10px 15px 13px 15px;
        margin-bottom: 1em;
        background-color: #333;
        border: solid 0;
        color: #F3F3F3;
        font-family: Courier, monospace !important;
        line-height: 18px;
        overflow: auto;
    }
    table.post-list {
        width: 100%;
        border-collapse: collapse;
    }
    table.post-list a {
        color: #262;
        font-weight: bold;
    }
    table.post-list th {
        text-align: left;
    }
    table.post-list td, table.post-list th {
        padding: 0.5em 0.3em;
        border-bottom: 1px solid #dba;
    }
    table.post-list tr:last-child td, table.post-list tr:last-child th {
        border-bottom: 0;
    }
    div.related {
        margin-bottom: 2em;
    }
    #footer {
        display:none !important;
    }
    .home_box {
        float: left;
    }
    #content .home_box h2 {
        background: #333;
        padding: 0.3em 0.5em;
        border-bottom: 3px solid #666;
        margin-bottom: 0em;
        color: #dd5;
    }
    .home_box h2 a {
        color: #ff3;
        margin: 0 0.2em;
    }
    #home_left {
        width: 29em;
    }
    #home_right {
        width: 16em;
        margin-left: 1em;
    }
    .clearfix {
        clear: both;
    }
    #footer a {
        color: #3E423A;
        font-weight: normal;
    }
    .announce {
        padding: 5px;
        padding-bottom: 20px;
        padding-top: 0px;
    }
    .home_box .post h1 a {
        font-size: 0.7em;
    }
    .repo {
        background: #fff5cc;
        border: 2px solid #dda;
        padding: 0.7em 1em;
        font-size: 0.8em;
        margin: 1em 1em 0 0;
        -moz-border-radius: 0.5em;
    }
    .repo h3 {
        margin: 0 0 0.3em 0;
    }
    .repo h3 a {
        font-weight: bold;
        color: black;
        font-size: 1.1em;
    }
    #github-projects.big .repo {
        float: left;
        width: 25.2em;
        margin: 1.2em 1em 0 0;
        display: inline-block;
        float: none;
        vertical-align: top;
    }
    .float-left {
        float: left;
    }
    .float-right {
        float: right;
    }
    a img {
        border: 0;
    }
    ul#toc {
        list-style: none;
        margin: 0;
    }
    ul#toc ul {
        list-style: none;
    }
    .book-ad a {
        color: #262;
        font-size: 1.5em;
        text-decoration: none;
    }
    #buy-book-toc {
        float: right;
        width: 200px;
    }
    #buy-book-toc img {
        width: 200px;
    }
    hr {
        border:1px solid #eee;
    }
    #nav {
        color:#fff;
    }
    #nav a:link, #nav a:visited, #nav a {
        text-decoration:none !important;
        border:none !important;
        font-size:1.5em !important;
        background-color:#eee;
        padding:.5em 1em;
    }
    #nav a:last-child {
        float:right !important;
        display:block;
    }
    #nav a:first-child {
        float:left !important;
        display:block;
    }
    header {
        display: none;
    }
    aside {
        display: none;
    }
    aside.sidebar {
        position: absolute;
        margin-top: 50px;
        margin-left: -100px;
        float: right;
    }
    aside > nav > ul {
        display: none;
    }
    aside > nav > hr.sidebar {
        display: none;
    }
    footer, aside > nav {
        text-align: right;
        zoom: 1;
        filter: alpha(opacity=50);
        opacity: 0.5;
    }
    #book-chapters {
        background-color: #F5F5F5 !important;
    }
}
